INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Logitech M650 Rose', 'chuot-logitech-m650-rose', 13,
        '{"Thương hiệu": "Logitech", "Model": "Logitech M650 Rose", "Bảo hành": "12 tháng", "Loại chuột": "Văn phòng", "Màu sắc": "Hồng", "Phần mềm tùy chọn": "Được hỗ trợ bởi Logitech Options+ trên macOS 10.15 trở lên và Windows 10, 11 trở lên", "Kết nối": "Không dây / Bluetooth", "Phạm vi không dây": "10 m", "Pin": "1 x pin AA", "Nút": "5", "Cảm biến": "Quang học", "DPI": "400 - 4000", "Trọng lượng": "101.4 g", "Kích thước (Cao x Rộng x Sâu)": "108.2 mm x 61 mm x 38.8 mm"}',
        'Chuột Logitech M650 Rose là sự kết hợp hoàn hảo giữa phong cách, sự thoải mái và hiệu suất vượt trội, được thiết kế để nâng cao trải nghiệm làm việc hàng ngày của bạn. Với tông màu hồng Rose tinh tế và hiện đại, chiếc chuột này không chỉ là một công cụ làm việc mà còn là một phụ kiện thời trang trên bàn làm việc. Điểm nhấn của M650 là công nghệ cuộn SmartWheel tiên tiến, cho phép chuyển đổi linh hoạt giữa chế độ cuộn từng dòng chính xác để xem tài liệu chi tiết và chế độ cuộn siêu nhanh (hyper-fast) để lướt qua các trang web dài dễ dàng. Thiết kế công thái học của M650 Rose được chăm chút tỉ mỉ để vừa vặn hoàn hảo trong lòng bàn tay cỡ vừa đến lớn, mang lại cảm giác thoải mái tối đa ngay cả khi sử dụng trong thời gian dài. Vùng đệm ngón cái bằng cao su mềm mại giúp giảm áp lực và tăng cường độ bám. Đặc biệt, công nghệ SilentTouch giảm thiểu 90% tiếng ồn khi nhấp chuột, giúp bạn tập trung làm việc mà không làm phiền những người xung quanh. Về khả năng kết nối, M650 Rose cung cấp sự linh hoạt với hai tùy chọn: qua bộ thu USB Logi Bolt an toàn hoặc qua Bluetooth Low Energy, đảm bảo tương thích rộng rãi với hầu hết các hệ điều hành phổ biến như Windows, macOS, Linux, Chrome OS, iPadOS và Android. Thời lượng pin ấn tượng lên đến 24 tháng chỉ với một viên pin AA giúp bạn yên tâm sử dụng lâu dài mà không lo gián đoạn. Hai nút bên hông có thể tùy chỉnh dễ dàng qua phần mềm Logi Options+, cho phép gán các chức năng yêu thích để tối ưu hóa quy trình làm việc. Logitech M650 Rose là lựa chọn lý tưởng cho những ai tìm kiếm sự thoải mái, hiệu quả và phong cách.',
