INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Pulsar có dây Xlite Medium White', 'chuot-pulsar-co-day-xlite-medium-white', 40,
        '{"Thương hiệu": "Pulsar", "Bảo hành": "24 tháng"}',
        'Đánh giá chi tiết chuột Pulsar Xlite Medium White\n\nLà một game thủ yêu thích form cầm của Zowie EC2 nhưng \"hầu bao\" không cho phép, thì Pulsar Xlite Medium White chắc chắn là lựa chọn không thể bỏ qua.\n\n**Thiết kế công thái học hoàn hảo**\n\nPulsar Xlite Medium White sở hữu thiết kế công thái học dành cho người thuận tay phải, có thể coi là phiên bản cải tiến và chỉnh sửa đôi chút từ Zowie EC2 - \"hoàng tử\" trong dòng chuột FPS. Các góc cạnh và lớp phủ đã được nâng cấp, mang đến trải nghiệm tốt nhất cho người dùng, đặc biệt là những ai chơi game trong thời gian dài. Khoác lên mình màu trắng thanh thoát, Pulsar Xlite Medium White còn là phụ kiện trang trí hoàn hảo cho không gian chơi game tông màu sáng.\n\n**Trọng lượng lý tưởng**\n\nVới trọng lượng chỉ 52g (±1g), Pulsar Xlite Medium White có mức cân nặng vô cùng cạnh tranh trong phân khúc. Thiết kế đặc trưng của Pulsar với các khoảng trống ở phần dưới thân chuột giúp đạt được trọng lượng tối ưu này.\n\n**Switch quang học bền bỉ**\n\nĐộ chính xác là yếu tố không thể thiếu trên chuột gaming hiện đại, và Pulsar Xlite Medium White không làm người dùng thất vọng. Nhờ switch quang học cao cấp, chuột đảm bảo độ chuẩn xác trong từng cú click và giảm thiểu tình trạng double-click.\n\n**Cảm biến PAW3395 cao cấp**\n\nPulsar trang bị cho Xlite Medium White cảm biến PAW3395, một trong những cảm biến đỉnh cao nhất hiện nay. Với 26000 DPI và 650 IPS, chuột mang đến cảm giác rê chuột chính xác nhất cho mọi tùy chỉnh. Chip xử lý bên trong đảm bảo hiệu năng tuyệt vời, mang lại lợi thế trong các trận chiến căng thẳng.\n\n**Dây cáp thông minh tối ưu trải nghiệm**\n\nLà chuột gaming có dây, Pulsar Xlite Medium White được thiết kế dây cáp với góc độ phù hợp, tối ưu cho các thao tác rê, kéo thả hay ghìm tâm. Dây dù được làm nhẹ nhất, không gây cản trở trong quá trình sử dụng.\n\n**Tùy chỉnh linh hoạt với phần mềm đi kèm**\n\nĐi kèm chuột là phần mềm giúp người dùng tùy chỉnh linh hoạt các cài đặt như macro, độ trễ. Đây là yếu tố không thể bỏ qua nếu bạn đang hướng tới mục tiêu \"go pro\" trong tựa game của mình.',
        1, 'https://product.hstatic.net/200000722513/product/pulsarxlitev3wiredgamingmouse_wh_542f4c86d1964fd5bb9e0e3a0a58221d.png'
    );
