INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Basilisk V3 Pro', 'chuot-razer-basilisk-v3-pro', 15,
        '{"Kết nối": "Có dây / Không dây / Bluetooth", "Thời lượng pin": "Lên đến 90 giờ (HyperSpeed)", "Đèn LED": "Razer Chroma RGB", "Cảm biến": "Quang học Focus Pro 30K", "DPI": "30000", "IPS": "750", "Số nút": "11", "Gia tốc tối đa (G)": "70", "Switch": "Optical Gen-3", "Tuổi thọ Switch": "90 triệu lần nhấp", "Feet chuột": "100% PTFE", "Kích thước (Dài x Rộng x Cao)": "130 mm x 75.4 mm x 42.5 mm", "Trọng lượng": "112 g"}',
        'Chuột Razer Basilisk V3 Pro là đỉnh cao của công nghệ chuột chơi game, được thiết kế để mang lại hiệu suất vượt trội và trải nghiệm tùy chỉnh không giới hạn cho các game thủ chuyên nghiệp và những người đam mê công nghệ. Với bộ cảm biến quang học Razer Focus Pro 30K tiên tiến, độ chính xác đến từng pixel được đảm bảo, cho phép bạn thực hiện những pha di chuyển nhanh và chính xác nhất. Công nghệ không dây Razer HyperSpeed thế hệ mới cung cấp kết nối siêu nhanh và ổn định, gần như không có độ trễ, đồng thời hỗ trợ kết nối đa thiết bị, giúp tối ưu hóa không gian làm việc của bạn.\n\nĐiểm nhấn đặc biệt của Basilisk V3 Pro là con lăn Razer HyperScroll Tilt Wheel, mang đến hai chế độ cuộn độc đáo: chế độ Smart-Reel cho khả năng cuộn có khấc chi tiết và chế độ Free-Spin cực nhanh, lý tưởng để duyệt web hay cuộn tài liệu dài. Hơn nữa, nó còn có chế độ ảo để tự động chuyển đổi giữa hai chế độ này.\n\n10+1 nút bấm có thể lập trình hoàn toàn, mỗi nút được trang bị công tắc quang học Razer Optical Mouse Switches Gen-3 siêu bền với tuổi thọ lên đến 90 triệu lần nhấn, đảm bảo phản hồi tức thì và không có lỗi nhấp đúp ngoài ý muốn.\n\nHệ thống chiếu sáng Razer Chroma RGB 13 vùng, bao gồm cả dải đèn dưới thân chuột, không chỉ tạo nên vẻ đẹp thẩm mỹ ấn tượng mà còn có thể đồng bộ hóa với các thiết bị Chroma khác, mang đến trải nghiệm chơi game đắm chìm. Thiết kế công thái học cải tiến ôm sát lòng bàn tay, giảm thiểu mệt mỏi trong những phiên chơi game dài.\n\nPin dung lượng cao cho phép bạn chơi game liên tục mà không lo gián đoạn. Basilisk V3 Pro còn tương thích với sạc không dây qua Razer Mouse Dock Pro (bán riêng) hoặc Wireless Charging Puck, mang lại sự tiện lợi tối đa. Đây thực sự là lựa chọn hoàn hảo cho những ai tìm kiếm sự kết hợp giữa tốc độ, độ chính xác và khả năng tùy biến cao.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot-recovered_757cce0149c042149f8b58f30fcb3307_8868cbe7339a46e9813f2eb8bdb334ee.png'
    );
