INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Pulsar không dây X2 H eS Wireless Size2 Black', 'chuot-pulsar-khong-day-x2-h-es-wireless-size2-black', 40,
        '{"Thương hiệu": "Pulsar", "Tên sản phẩm": "Pulsar X2 H eS Wireless Size 2", "Thiết kế": "Đối xứng", "Cảm biến": "PixArt PAW3395", "Độ phân giải (DPI)": "26000 DPI", "Màu sắc": "Đen", "Số lượng nút bấm": "8", "Switch": "Optical Switch", "Đèn LED": "Không", "Kết nối": "Wireless 2.4GHz / Dây USB-C to A", "Kích thước": "120.4 x 63 x 38 mm", "Trọng lượng": "69g", "Pin": "500mAh", "Thời lượng pin": "70–100 giờ sử dụng", "Nút cuộn": "Blue Encoder – Hợp kim nhôm cao cấp", "Công nghệ cảm biến": "PixArt Motion Sync Technology", "Màn hình OLED": "Có – Tùy chỉnh trực tiếp không cần phần mềm", "Polling Rate": "4000Hz (Wireless) / 8000Hz (Cắm dây)"}',
        'Chuột không dây Pulsar X2 H eS Wireless Size 2 nổi bật hơn so với chuột thông thường ở những tính năng độc đáo. Nó được trang bị màn hình OLED ở mặt dưới, cho phép tùy chỉnh nhanh chóng và không cần driver. Người dùng có thể kiểm tra trạng thái pin và định cấu hình các cài đặt khác nhau như polling rate, LOD (Khoảng cách nhấc chuột), Đồng bộ hóa chuyển động, DPI (400, 800, 1600, 3200, 6400 hoặc 12800)...Thiết kế đối xứng đặc biệt tiện lợi dành cho người thuận cả tay trái lẫn tay phải, với các nút phụ có thể gán chức năng, nâng cấp trải nghiệm chơi game của bạn lên một tầm cao mới.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 1\n\nThiết kế cao cấp\nTối đa hóa tốc độ và độ chính xác: Trải nghiệm chơi game ở đỉnh cao với tốc độ phản hồi 4000Hz cao ấp. Receiver 4K tiên tiến đảm bảo khả năng phản hồi liền mạch, mang lại kết nối không bị lag để phản ứng nhanh như chớp và độ chính xác tuyệt vời.\nChuyển đổi tốc độ nhanh chóng: Không dây sẵn sàng 4K cho tín hiệu sắc nét và khả năng phản hồi nhanh. Chuyển sang chế độ có dây 8K để có độ chi tiết và độ chính xác tuyệt vời. Tận hưởng lợi ích nâng cao của tốc độ 4Khz để tăng cường độ rõ nét và 8Khz để có độ chính xác cao nhất khi chơi game.\nNút hông có thể tháo rời: 4 nút phụ bên hông có nắp phím có thể tháo rời dạng nam châm, phù hợp cho các nhu cầu sử dụng khác nhau.\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 2\n\nMàn hình Oled\nKhám phá khả năng tùy chỉnh nâng cao với màn hình OLED tích hợp. Dễ dàng theo dõi mức pin và điều chỉnh các cài đặt chính, chẳng hạn như LOD, thời gian gỡ lỗi, đồng bộ hóa chuyển động, tốc độ bỏ phiếu và DPI, bằng cách sử dụng các nút chuột chuyên dụng – tất cả đều không yêu cầu trình điều khiển bên ngoài.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 3\n\nNút cuộn chất lượng cao\nKhám phá độ bền và phong cách với bánh xe hợp kim nhôm cao cấp. Thiết kế đẹp mắt đảm bảo độ chính xác và tuổi thọ cao, mang lại trải nghiệm cuộn mạnh mẽ. Bộ mã hóa Pulsar Blue mang lại cảm giác xoay xúc giác thú vị, hoạt động êm ái và mượt mà, với cơ chế chống bụi tích hợp để nâng cao độ tin cậy.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 4\n\nSwitch Optical\nSwitch bấm quang học ngăn chặn lỗi Double Click bằng tính năng phát hiện dựa trên ánh sáng và cung cấp tốc độ nhanh với click trước được sắp xếp theo 3 giai đoạn.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 5\n\nMắt cảm biến cao cấp\nTrang bị cảm biến PAW3395 hàng đầu mới nhất của PixArt. Với các khả năng ấn tượng, bao gồm tốc độ cao với 26000 dpi, 650 IPS và gia tốc 50G, nó được tối ưu hóa để mang lại trải nghiệm chơi game cạnh tranh đỉnh cao. Bộ xử lý chip chính đã được nâng cấp lên Nordic MCU cao cấp, mang lại hiệu suất năng lượng vượt trội và khả năng xử lý nhanh, chính xác.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 6\n\nTrải nghiệm chơi game hoàn hảo với các chế độ DPI tùy chỉnh trên chuột của bạn. Sử dụng bánh xe cuộn để điều chỉnh nhanh chóng và dễ dàng cài đặt DPI, tìm độ nhạy lý tưởng cho bạn. Chế độ DPI tùy chỉnh cho phép bạn đặt DPI theo mức tăng 50, cho phép bạn kiểm soát chính xác trải nghiệm chơi trò chơi của mình.\n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 7\n\nCông nghệ PixArt Motion Sync: Cảm biến cao cấp mới  của Pixart có thông số kỹ thuật cao nhất trên thị trường với độ chính xác cực cao lên tới 99,6%. Tuy nhiên, điều này không có nghĩa là cảm biến theo dõi chính xác hơn. Để giúp cảm biến của bạn theo dõi chính xác hơn, Công nghệ đồng bộ hóa chuyển động được tích hợp vào cảm biến. PC của bạn trích xuất thông tin từ một cảm biến khác với tốc độ không hoàn toàn khớp với tín hiệu mà chuột gửi đi khi nó trích xuất thông tin từ một cảm biến khác. Công nghệ đồng bộ hóa chuyển động đồng bộ hóa tín hiệu của nó một cách thông minh theo các khoảng thời gian chính xác mà máy tính của bạn trích xuất thông tin. Điều này cho phép việc theo dõi rất chính xác. Tuy nhiên, điều này có thể gây ra một số độ trễ chuyển động (dưới 1ms) mà bạn không thể nhận thấy khi chơi trong trò chơi. \n\nChuột không dây Pulsar X2 H eS Wireless Size 2-Black 8\n\n4K Polling Ready\nTrải nghiệm chuyển động con trỏ nâng cao với Dongle không dây Pulsar 4K mới, mang lại hiệu suất mượt mà và sắc nét hơn. Đặc biệt có giá trị đối với màn hình có refresh rate cao, polling rate cao đảm bảo luồng dữ liệu cập nhật hơn, hoàn toàn phù hợp với tần số nhanh hơn của màn hình. Bằng cách cung cấp số lượng báo cáo dữ liệu nhiều hơn tới 4 lần mỗi giây, độ trễ giữa báo cáo mới nhất và kết xuất khung hình trên màn hình của bạn giảm đáng kể so với tốc độ bỏ phiếu 1000 Hz, mang lại trải nghiệm di chuột liền mạch và cập nhật.',
        1, 'https://product.hstatic.net/200000722513/product/pulsarx2hesgamingmouse_black_005_d7ed3cd06b05498a9c1d535cb2cf7c23.png'
    );
