INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột không dây Rapoo M20', 'chuot-khong-day-rapoo-m20', 29,
        '{"Thương hiệu": "Rapoo", "Model": "M20", "Loại chuột": "Chuột không dây", "Kết nối": "Không dây 2.4GHz", "Khoảng cách hoạt động": "10m", "Độ phân giải": "1000 DPI", "Số nút": "3", "Nguồn pin": "1 x AA", "Thời lượng pin": "Lên đến 12 tháng", "Màu sắc": "Đen", "Kích thước": "100.4 x 60.8 x 38.5 mm", "Trọng lượng": "75g", "Tương thích": "Windows, macOS, Chrome OS", "Bảo hành": "24 tháng"}',
        'Chuột không dây Rapoo M20 là sự lựa chọn lý tưởng cho mọi nhu cầu làm việc và giải trí hàng ngày, mang đến sự tiện lợi và hiệu quả tối ưu. Với thiết kế nhỏ gọn, tinh tế và hiện đại, M20 dễ dàng bỏ vào túi và trở thành người bạn đồng hành không thể thiếu cho những người thường xuyên di chuyển. Kiểu dáng công thái học đối xứng cho phép người dùng thuận tay trái hay tay phải đều có thể sử dụng một cách thoải mái trong thời gian dài mà không gây mỏi.\n\nKết nối không dây 2.4GHz tiên tiến đảm bảo đường truyền ổn định và mạnh mẽ, loại bỏ hoàn toàn sự vướng víu của dây cáp. Chỉ cần cắm đầu thu USB siêu nhỏ vào máy tính, bạn có thể bắt đầu sử dụng ngay lập tức mà không cần cài đặt phần mềm phức tạp. Phạm vi hoạt động hiệu quả lên đến 10 mét, mang lại sự linh hoạt tuyệt vời cho không gian làm việc của bạn, dù là ở văn phòng, tại nhà hay khi di chuyển.\n\nRapoo M20 được trang bị cảm biến quang học chất lượng cao với độ phân giải 1000 DPI, cung cấp khả năng di chuyển con trỏ chính xác và mượt mà trên nhiều bề mặt khác nhau, từ bàn làm việc thông thường đến bề mặt kính. Điều này rất phù hợp cho các tác vụ văn phòng đòi hỏi sự tỉ mỉ, duyệt web hay các công việc thiết kế cơ bản. Đặc biệt, tính năng tiết kiệm năng lượng thông minh giúp kéo dài tuổi thọ pin lên đến 9 tháng (tùy thuộc vào cách sử dụng và môi trường), giảm thiểu tần suất thay pin, mang lại sự tiện lợi và an tâm tối đa cho người dùng. Chuột tương thích rộng rãi với nhiều hệ điều hành phổ biến như Windows, macOS và Chrome OS, đảm bảo khả năng kết nối linh hoạt với hầu hết các thiết bị máy tính hiện nay. Với Rapoo M20, bạn sẽ trải nghiệm sự mượt mà, chính xác và thoải mái trong từng cú nhấp chuột.',
        1, 'https://product.hstatic.net/200000722513/product/chuot-khong-day-rapoo-m20_2__1__bea49e83652c4d4e89a5f4db87036148.png'
    );
