INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Basilisk V3 Pro White', 'chuot-razer-basilisk-v3-pro-white', 15,
        '{"Kết nối": "Có dây / Không dây / Bluetooth", "Thời lượng pin": "Lên đến 90 giờ (HyperSpeed)", "Đèn LED": "Razer Chroma RGB", "Cảm biến": "Quang học Focus Pro 30K", "DPI": "30000", "IPS": "750", "Số nút bấm": "11", "Gia tốc tối đa (G)": "70", "Switch": "Optical Gen-3", "Tuổi thọ Switch": "90 triệu lần nhấp", "Feet chuột": "100% PTFE", "Kích thước (Dài x Rộng x Cao)": "130 mm x 75.4 mm x 42.5 mm", "Trọng lượng": "112 g"}',
        'Chuột Razer Basilisk V3 Pro White là biểu tượng của sự sang trọng, hiệu suất vượt trội và khả năng tùy biến tối đa, được thiết kế dành cho những game thủ và người dùng chuyên nghiệp đòi hỏi cao nhất. Với gam màu trắng tinh tế, Basilisk V3 Pro không chỉ là một công cụ mạnh mẽ mà còn là một điểm nhấn thẩm mỹ cho không gian làm việc hay chơi game của bạn.\n\nTrái tim của Basilisk V3 Pro là Cảm biến quang học Razer™ Focus Pro 30K, mang lại độ chính xác không tưởng lên đến 30.000 DPI, tốc độ theo dõi 750 IPS và gia tốc 70G, đảm bảo mọi chuyển động nhỏ nhất của bạn đều được ghi nhận hoàn hảo.\n\nKết hợp với Công tắc quang học Razer™ Gen-3, chuột cung cấp tốc độ phản hồi cực nhanh 0.2ms và tuổi thọ lên đến 90 triệu lần nhấp, loại bỏ hoàn toàn vấn đề nhấp đúp ngoài ý muốn và mang lại cảm giác nhấp rõ ràng, sắc nét.\n\nĐiểm nổi bật khác là Bánh xe cuộn Razer™ HyperScroll Tilt Wheel cải tiến, cho phép chuyển đổi linh hoạt giữa chế độ cuộn tự do tốc độ cao (free-spin) và cuộn xúc giác có độ chính xác cao (tactile cycling). Chế độ Smart-Reel thông minh có thể tự động chuyển đổi giữa hai chế độ tùy theo tốc độ cuộn của bạn.\n\nVề kết nối, Basilisk V3 Pro cung cấp sự linh hoạt tối đa với công nghệ Razer™ HyperSpeed Wireless siêu tốc, Bluetooth tiện lợi và kết nối có dây qua USB-C. Tận hưởng thời lượng pin ấn tượng, cùng khả năng sạc không dây qua Razer Mouse Dock Pro và Wireless Charging Puck (bán riêng).\n\nThiết kế công thái học cùng 11 nút bấm có thể lập trình, bao gồm cả nút đa chức năng đặc trưng của dòng Basilisk, cho phép bạn tùy chỉnh mọi lệnh và macro theo ý muốn.\n\nHệ thống chiếu sáng Razer Chroma™ RGB với 13 vùng sáng có thể tùy chỉnh độc lập, tạo nên hiệu ứng ánh sáng sống động và cá nhân hóa sâu sắc, biến chuột của bạn thành một tác phẩm nghệ thuật ánh sáng.\n\nRazer Basilisk V3 Pro White thực sự là một vũ khí tối thượng cho mọi trận chiến, mang lại trải nghiệm không giới hạn và giúp bạn vượt qua mọi thử thách.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot-recovered_44dcc374ea4d40e9bfaa4fa0d086f62f_c213a6395356408aae12d54abc6ff8ee.gif'
    );
