INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Viper V3 HyperSpeed', 'chuot-razer-viper-v3-hyperspeed', 15,
        '{"Hãng sản xuất": "Razer", "Bảo hành": "24 tháng", "Model": "Chuột Razer Viper V3 HyperSpeed", "Màu sắc": "Đen", "Kiểu kết nối": "Không dây Hyperspeed", "Thời gian sử dụng": "Lên đến 285 giờ (không dây); Lên đến 70 giờ (Không dây 4000 Hz) Dựa trên thời lượng pin AA", "Kiểu thiết kế": "Đối xứng", "Độ nhạy (DPI)": "30.000", "Cảm biến": "Cảm biến quang học Focus Pro 30K", "Số nút bấm": "6 nút", "Switch": "Razer Switch Gen-2 (60 triệu lần nhấn)", "Kích thước": "127 x 64 x 40 mm (Dài x Rộng x Cao)"}',
        'Chuột Razer Viper V3 HyperSpeed là một biểu tượng mới trong thế giới gaming, được thiết kế chuyên biệt để mang lại lợi thế cạnh tranh tối đa cho game thủ eSports. Với công nghệ không dây Razer HyperSpeed tiên tiến, chuột đảm bảo độ trễ cực thấp và kết nối ổn định, giúp mọi thao tác di chuyển chuột đều được phản hồi tức thì, không bỏ lỡ bất kỳ khoảnh khắc quan trọng nào trong trận đấu.\n\nSức mạnh cốt lõi của Viper V3 HyperSpeed đến từ cảm biến quang học Razer Focus Pro 30K, mang lại độ chính xác hàng đầu ngành với DPI tối đa 30.000, tốc độ theo dõi 750 IPS và gia tốc 70G. Điều này cho phép người dùng tùy chỉnh độ nhạy cực kỳ linh hoạt để phù hợp với mọi phong cách chơi và tựa game.\n\nThiết kế siêu nhẹ chỉ 82g (không pin) cùng với hình dáng được tối ưu hóa sau nhiều nghiên cứu và phản hồi từ các tuyển thủ chuyên nghiệp, mang lại cảm giác cầm nắm thoải mái và linh hoạt, giảm thiểu mỏi tay ngay cả trong những phiên chơi kéo dài.\n\nTuổi thọ pin ấn tượng lên đến 280 giờ chỉ với một viên pin AA, giúp game thủ không còn phải lo lắng về việc hết pin giữa chừng. Chuột còn được trang bị Switch quang học Razer Gen-2, đảm bảo tuổi thọ lên đến 60 triệu lần nhấp với tốc độ phản hồi chớp nhoáng và không có hiện tượng click đúp ngoài ý muốn.\n\nRazer Viper V3 HyperSpeed thực sự là một vũ khí đáng gờm, sẵn sàng đưa bạn lên đỉnh cao của mọi bảng xếp hạng.',
        1, 'https://product.hstatic.net/200000722513/product/1_21b147133b854b80989cbcbd8fa9a449.jpg'
    );
