INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Asus ROG Harpe Ace Aim Lab White', 'chuot-asus-rog-harpe-ace-aim-lab-white', 3,
        '{"Thương hiệu": "Asus", "Model": "ROG Harpe Ace Aim Lab Edition", "Đặc điểm": "Cực nhẹ, cảm biến ROG Aimpoint độc quyền 36000 dpi, công nghệ ROG SpeedNova, hỗ trợ Aura Sync", "Màu sắc": "Đen", "Kiểu dáng": "Đối xứng", "Số nút": "5", "Switch chuột": "ROG, tuổi thọ lên đến 70 triệu lần nhấn", "Kết nối": "Có dây, Bluetooth 5.1, Wireless 2.4GHz", "Pin": "Li-Ion", "Thời lượng pin": "90 giờ (không LED), 79 giờ (có LED). Lưu ý: Thời lượng pin có thể thay đổi tùy theo nhu cầu và mục đích sử dụng.", "Cảm biến": "ROG AimPoint", "DPI": "36000", "IPS": "650", "Gia tốc": "50G", "AURA Sync": "Có", "Phần mềm": "Armoury Crate", "Kích thước": "127.5(L) x 63.7(W) x 39.6(H) mm", "Trọng lượng": "54g"}',
        'Chuột Asus ROG Harpe Ace Aim Lab White là một kiệt tác kỹ thuật, được thiết kế dành riêng cho các game thủ chuyên nghiệp và những người đam mê esports khao khát sự chính xác và tốc độ đỉnh cao. Với trọng lượng siêu nhẹ chỉ 54 gram, chiếc chuột này mang lại khả năng di chuyển nhanh như chớp và không hề gây mỏi tay, ngay cả trong những phiên chơi game kéo dài. Điểm nhấn là cảm biến quang học ROG AimPoint tiên tiến, cung cấp độ phân giải lên đến 36.000 DPI, tốc độ 650 IPS và gia tốc 50g, đảm bảo mọi cử động của bạn đều được ghi nhận với độ chính xác tuyệt đối. Sản phẩm này là kết quả của sự hợp tác chiến lược với Aim Lab, tối ưu hóa để nâng cao kỹ năng aim và hiệu suất thi đấu. Khả năng kết nối ba chế độ linh hoạt – không dây 2.4 GHz với công nghệ ROG SpeedNova siêu tốc độ, Bluetooth tiện lợi, và có dây USB để sạc hoặc chơi game liên tục – mang lại sự linh hoạt tối đa. Công tắc ROG Micro Switches bền bỉ với tuổi thọ 70 triệu lần nhấn cùng dây ROG Paracord siêu mềm và chân chuột PTFE 100% đảm bảo trải nghiệm lướt mượt mà. Thiết kế màu trắng tinh tế, hình dáng đối xứng ôm sát tay, phù hợp với nhiều kiểu cầm chuột, biến ROG Harpe Ace Aim Lab White không chỉ là một thiết bị ngoại vi, mà còn là người bạn đồng hành đắc lực trên mọi chiến trường ảo.',
        1, 'https://product.hstatic.net/200000722513/product/rog-harpe-ace-aim-lab-edition-moonlight-white-01_69a288ba00e3493f923276f245c30461.jpg'
    );
