INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Steelseries Rival 3', 'chuot-steelseries-rival-3', 18,
        '{"Hãng sản xuất": "Steelseries", "Bảo hành": "12 tháng", "Màu sắc": "Đen", "Kiểu kết nối": "Có dây", "Kiểu cầm": "Fingertip, Claw", "Đèn LED": "3 vùng sáng RGB", "Kiểu thiết kế": "Công thái học cho tay phải", "Cảm biến": "SteelSeries TrueMove Core (Quang học)", "Kích thước": "120 x 58 x 21 (mm) (Dài x Rộng x Cao)", "Khối lượng": "77g", "Chiều dài cáp": "1.8m"}',
        'Chuột Gaming Steelseries Rival 3 không chỉ đơn thuần là một sản phẩm chuột máy tính có dây thông thường mà còn là tác phẩm công nghệ cao cấp, được chế tạo dành riêng cho game thủ. Với thiết kế đối xứng phù hợp cho người thuận tay phải và các nút bấm được bố trí thông minh, Rival 3 hứa hẹn mang lại trải nghiệm chơi game mượt mà và thoải mái hàng đầu. Cùng với đó, sự kết hợp giữa đèn LED RGB và cảm biến TrueMove Core không những tôn vinh vẻ đẹp thiết kế mà còn nâng cao hiệu suất sử dụng.\n\nKết nối ổn định, mượt mà thông qua cổng ghép nối USB\nKết nối mượt mà và ổn định nhờ vào dây dẫn USB chất lượng cao là một trong những yếu tố quan trọng tạo nên tên tuổi của chuột Gaming Steelseries Rival 3 trên thị trường chuột máy tính. Với ưu điểm về khả năng ghép nối này, Steelseries Rival 3 đảm bảo rằng mọi chuyển động và click chuột được truyền đi một cách nhanh chóng chính xác mà không gây trễ hay gián đoạn, mang lại lợi thế cạnh tranh trong mọi trận đấu.\n\nKết nối chuột Gaming Steelseries Rival 3\nĐộ nhạy di chuột cao, làm chủ mọi trận đấu\nVới cảm biến TrueMove Core hiện đại, chuột Gaming Steelseries Rival 3 cung cấp độ nhạy DPI ấn tượng, có thể điều chỉnh từ 200 đến 8500 CPI. Chính ưu điểm này đã cho phép game thủ tinh chỉnh mức độ nhạy theo sở thích cá nhân hoặc yêu cầu của từng tựa game, đảm bảo độ chính xác tuyệt vời và phản ứng nhanh chóng trong ở tình huống.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot_e01eec6957cc40a88aba550b80cffed2_74ec8f2dec0447c382614fa201a4fa93.png'
    );
