INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Không Dây Rapoo M21 Silent', 'chuot-khong-day-rapoo-m21-silent', 29,
        '{"Thương hiệu": "Rapoo", "Model": "Rapoo M21 Silent", "Màu": "Đen", "Chuẩn giao tiếp": "Wireless 2.4Ghz", "Kiểu thiết kế": "Đối xứng", "DPI": "1000", "Tương thích": "Windows, MacOS"}',
        'Chuột máy tính không dây là một trong những lựa chọn hàng đầu khi có nhu cầu học tập, làm việc và giải trí ở bất kỳ đâu. Vì sự tiện lợi và dễ dàng kết nối với nhiều thiết bị khác nên đây luôn là thiết bị ngoại vi được nhiều người dùng tin chọn.\n\nBên cạnh việc mang đến những dòng sản phẩm tai nghe máy tính đã làm nên thương hiệu của mình, Rapoo còn cung cấp cho thị trường gaming gear những sản phẩm chuột không dây với độ trễ thấp, thiết kế nhỏ gọn, tiện nghi giúp bạn dễ dàng di chuyển và làm việc ở bất kỳ đâu.\n\n**Thiết kế công thái học, ôm sát lòng bàn tay**\n\nRapoo M21 Silent sở hữu thiết kế đối xứng, ôm sát lòng bàn tay giúp bạn có những thao tác sử dụng dễ dàng và thoải mái trong nhiều giờ liền. Điều này giúp người dùng thao tác vững vàng, hạn chế trượt tuột và không gây mỏi tay. Đặc biệt, đây là một trong những dòng sản phẩm chuột máy tính có thể sử dụng cho cả tay trái và tay phải tùy thuộc vào thói quen của từng người.\n\n**Công nghệ hiện đại đáp ứng tốt mọi nhu cầu sử dụng**\n\nNếu bạn yêu thích các thiết bị không dây như bàn phím, loa máy tính vì sự tiện lợi và giúp góc làm việc, học tập gọn gàng thì Rapoo M21 Silent sẽ là một lựa chọn không nên bỏ qua.\n\nĐể việc kết nối nhanh chóng và hỗ trợ tốt nhất cho công việc, Rapoo M21 Silent trang bị công nghệ không dây 2.4GHz, giúp bạn sử dụng chuột dễ dàng với phạm vi hoạt động lên đến 10m.\n\nNgoài ra, việc sử dụng nút bấm silent giúp giảm thiểu độ ồn nhưng vẫn cho cảm giác click êm ái khi thao tác làm việc, học tập. Con lăn mượt mà cho phép lướt nhanh qua các trang dài, giúp người dùng dễ dàng khi cần đọc tài liệu, xem báo, tìm kiếm thông tin.\n\n**Kết nối ổn định qua công nghệ không dây 2.4GHz**\n\nRapoo M21 Silent kết nối với thiết bị qua công nghệ wireless 2.4GHz mới nhất, hỗ trợ khoảng cách kết nối đến 10m, mang đến độ ổn định cao, tránh tín hiệu chập chờn và tương thích với nhiều hệ điều hành.\n\n**Nút bấm tiện lợi hỗ trợ xử lý công việc hiệu quả**\n\nChuột được tích hợp 3 nút cơ bản nhưng vô cùng tiện lợi, phù hợp cho các công việc đơn giản. Tốc độ phản hồi nhanh giúp bạn thực hiện các thao tác nhanh chóng và xử lý công việc hiệu quả.\n\n*   Con lăn cuộn trang chính xác.\n*   Phím bấm trái/phải êm ái, chịu được lực bấm tốt.\n\n**Chỉ số DPI**\n\nChuột không dây Rapoo M21 Silent có độ phân giải lên đến 1000 DPI, mang đến sự chính xác và thoải mái khi di chuột, đáp ứng tốt nhu cầu sử dụng từ học tập, làm việc cho đến chơi game giải trí trên nhiều thiết bị khác nhau.\n\n**Thời gian sử dụng**\n\nVề thời gian sử dụng, chỉ với 1 viên pin AA cùng công nghệ tiết kiệm pin mới nhất của Rapoo, bạn có thể sử dụng chuột từ 9 - 12 tháng. Thời gian này có thể kéo dài hoặc ngắn hơn tùy thuộc vào tần suất và cách bạn sử dụng. Ngoài ra, Rapoo M21 Silent còn trang bị nút tắt chuột ON/OFF bên dưới thân chuột khi không cần sử dụng để giảm hao phí pin hiệu quả.',
        1, 'https://product.hstatic.net/200000722513/product/thumbchuot_319f928f991a4303a119531a028fca35_caa118908bcb4634847506512cb92ce8.png'
    );
