INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Pulsar có dây X2A Medium Black', 'chuot-pulsar-co-day-x2a-medium-black', 40,
        '{"Kích thước": "120.4mm x 63mm x 38mm", "Trọng lượng": "57g (±1g)", "Dáng chuột": "Đối xứng - Ambidextrous, Side button cover 4pcs", "Switch": "Optical Switch, Pulsar Blue Encoder", "Con lăn": "Super Smooth ballbearing wheels", "Cảm biến": "PAW3395, 26,000 DPI, 650 IPS, 50g gia tốc", "Kết nối": "Wireless 2.4GHz, Lag-free, USB-C", "Thông số khác": "Thiết kế Wide Waist / Low Hump, Symmetrical Shape, Recommended for Claw/Fingertip Grip, Ultra-light Weight, Fully customizable sensor and keys, Superflex Paracord Cable, Up to 100 hours battery life (±10% at 1000Hz)", "Phụ kiện kèm theo": "Side button cover x4, Wireless Receiver x1, Wireless Receiver adapter x1, USB-C Cable x1", "Yêu cầu hệ thống": "USB Port, Windows/Mac/Linux, Internet connection for software, Windows 7 or higher for Pulsar Software"}',
        'Chuột Pulsar X2A Medium Black có dây là lựa chọn hoàn hảo dành cho những game thủ và người dùng chuyên nghiệp tìm kiếm sự kết hợp giữa hiệu suất vượt trội, độ bền cao và thiết kế công thái học tối ưu. Với trọng lượng chỉ 53 gram, X2A Medium Black mang lại cảm giác cầm nắm thoải mái và linh hoạt tuyệt đối, giúp bạn thực hiện các pha di chuyển chuột nhanh và chính xác mà không gây mỏi tay trong thời gian dài sử dụng.\n\nTrang bị cảm biến quang học cao cấp PixArt PAW3395, X2A Medium Black đảm bảo độ chính xác tuyệt đối, tốc độ theo dõi ấn tượng và độ trễ gần như bằng không. Dù bạn đang thực hiện các pha flick-shot trong game FPS hay cần sự tỉ mỉ trong các tác vụ thiết kế đồ họa, con chuột này đều đáp ứng một cách hoàn hảo.\n\nCác nút bấm chính được trang bị switch quang học chất lượng cao, mang lại phản hồi xúc giác rõ ràng, độ bền vượt trội lên đến hàng chục triệu lần nhấn và loại bỏ hoàn toàn hiện tượng double-clicking không mong muốn. Thiết kế đối xứng (ambidextrous) của X2A Medium Black phù hợp với nhiều kiểu cầm chuột khác nhau, từ claw grip đến palm grip, đảm bảo sự thoải mái cho cả người dùng tay trái và tay phải.\n\nDây cáp paracord siêu mềm và linh hoạt giúp giảm thiểu ma sát, mang lại cảm giác di chuột như không dây. Lớp vỏ màu đen tuyền sang trọng không chỉ tăng tính thẩm mỹ mà còn chống bám bẩn hiệu quả. Pulsar X2A Medium Black thực sự là một vũ khí đáng tin cậy giúp bạn chinh phục mọi thử thách.',
        1, 'https://product.hstatic.net/200000722513/product/87139_chuot_co_day_pulsar_x2a_wi_c3cf98ce24664d8e86b878dcdf433a36.png'
    );
