INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Basilisk V3 Pro 35K Black', 'chuot-razer-basilisk-v3-pro-35k-black', 15,
        '{"Tên sản phẩm": "Razer Basilisk V3 Pro 35K", "Màu sắc": "Đen", "Cảm biến": "Razer Focus Pro 30K Optical Sensor", "DPI tối đa": "30.000", "Tốc độ theo dõi tối đa": "750 IPS", "Tăng tốc tối đa": "70 G", "Số nút lập trình": "11", "Switch chuột": "Razer Optical Mouse Switch Gen-3", "Tuổi thọ switch": "90 triệu lượt nhấn", "Độ phản hồi": "1000Hz", "Bộ nhớ tích hợp": "5 Profile", "Đèn Chroma RGB": "11 vùng", "Cuộn chuột": "Razer HyperScroll Tilt Wheel", "Kết nối": "Razer HyperSpeed Wireless (2.4GHz), Bluetooth, Có dây (USB-C)", "Thời lượng pin": "Lên đến 110 giờ (HyperSpeed Wireless)", "Tương thích hệ điều hành": "Windows, macOS, Android, iOS", "Phần mềm đi kèm": "Razer Synapse 3", "Trọng lượng": "112g (không bao gồm cáp)"}',
        'Nâng tầm trải nghiệm chơi game với Razer Basilisk V3 Pro 35K – chuột chơi game không dây RGB công thái học với khả năng tùy chỉnh và công nghệ tiên tiến nhất. Với cảm biến chính xác hoàn hảo và con lăn chuột với các tùy chọn cấu hình chuyên sâu, chơi game theo cách của bạn chưa bao giờ thú vị đến thế.\n\n**Cuộn lăn Razer™ HyperScroll Tilt**\nCuộn lăn 4 chiều với 3 chế độ độc đáo: Chọn giữa cuộn khấc Tactile-Cycling và cuộn tự do Free-Spin, hoặc tự động chuyển đổi giữa hai chế độ dựa trên tốc độ cuộn của bạn với chế độ cuộn thông minh Smart-Reel – hiện có thể cấu hình trên Synapse.\n\n**Cảm biến quang học Razer™ Focus Pro 35K Gen-2**\nĐộ chính xác hàng đầu: Trải nghiệm hiệu suất theo dõi hàng đầu trên nhiều bề mặt khác nhau, bao gồm cả kính, được hỗ trợ bởi các chức năng thông minh và điều chỉnh từng bước 1-DPI cho khả năng kiểm soát chi tiết và ngắm bắn chính xác hơn.\n\n**Lên đến 140 tiếng kết nối không dây Razer™ HyperSpeed**\nChơi game hiệu suất cao, độ trễ thấp: Tận hưởng hiệu suất mượt mà, không giật lag cho các phiên chơi game liên tục hoặc tận dụng sức mạnh thực sự của tốc độ thăm dò 8000Hz với Razer Mouse Dock Pro hoặc HyperPolling Wireless Dongle (cả hai đều được bán riêng).\n\n**13 thao tác điều khiển có thể tùy biến**\nKhả năng tùy chỉnh vô hạn: Gán vô số lệnh và macro trên 13 nút điều khiển ở vị trí dễ tiếp cận – bao gồm cò bấm đa chức năng nổi tiếng – để thực hiện các hành động quan trọng như push-to-talk, ping và nhiều hơn nữa.\n\n**Đèn Chroma 13 vùng với dải sáng dưới bao quanh**\nĐược hỗ trợ bởi Razer Chroma™ RGB: Tùy chỉnh từng vùng với hơn 16,8 triệu màu và vô số hiệu ứng ánh sáng, và đắm chìm vào những màn game mãn nhãn với sắc màu sống động, chuyển động nhịp nhàng với hơn 200 trò chơi tương thích với Chroma.\n\n**Công tắc chuột quang học Razer™ Gen-3**\nĐộ bền và tốc độ vô song: Được nâng cấp với vòng đời 90 triệu lần nhấp mà không gặp vấn đề nhấp đúp, cùng tốc độ kích hoạt 0,2 mili giây không có độ trễ, tận hưởng độ tin cậy và tốc độ vượt trội để sẵn sàng out trình đối thủ.',
        1, 'https://product.hstatic.net/200000722513/product/chuot-gaming-khong-day-razer-bas_62ede1e4e4434715b536f14dce75f95d.png'
    );
