INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột Razer Cobra Pro Trắng', 'chuot-razer-cobra-pro-trang', 15,
        '{"Hãng sản xuất": "Razer", "Model": "Razer Cobra Pro", "Số nút": "8 nút có thể lập trình", "RGB LIGHTING": "Razer Chroma™ RGB (con lăn, logo, nhiều vùng bên dưới chuột)", "Pin": "Lên đến 100 giờ với Razer HyperSpeed Wireless (1000Hz). Lên đến 33 giờ với Razer HyperPolling Wireless Dongle và Mouse Dock Pro* (4000Hz). Lưu ý: Sản phẩm không bao gồm HyperPolling Wireless Dongle và Mouse Dock Pro. Lên đến 170 giờ với Bluetooth.", "Kết nối": "Razer™ HyperSpeed Wireless (2.4 GHz), Bluetooth, Có dây – Dây Speedflex USB Type C", "Thiết kế": "Đối xứng - cho người thuận tay phải", "Độ phân giải (CPI/DPI)": "30.000 DPI", "Gia tốc": "750 IPS / 70G", "Kích thước": "127.0 x 61.7 x 42.7 mm", "Trọng lượng": "77g"}',
        'Nâng tầm đẳng cấp gaming của bạn với Chuột Razer Cobra Pro Trắng – một kiệt tác kết hợp hoàn hảo giữa phong cách tinh tế và hiệu năng đỉnh cao. Với sắc trắng thanh lịch, chiếc chuột này không chỉ là một công cụ, mà còn là một tuyên ngôn thẩm mỹ, nổi bật trên mọi góc setup gaming. Bên dưới vẻ ngoài trang nhã là sức mạnh đáng gờm: cảm biến quang học Razer Focus Pro 30K mang lại độ chính xác tuyệt đối, cho phép bạn thực hiện những pha di chuyển mượt mà và chuẩn xác đến từng pixel. Trang bị Switch quang học Razer Gen-3, Cobra Pro tự hào với tốc độ phản hồi tức thì và độ bền vượt trội, sẵn sàng cùng bạn chinh phục mọi thử thách. Kết nối không dây HyperSpeed siêu tốc đảm bảo độ trễ gần như bằng không, trong khi tùy chọn Bluetooth hoặc dây Speedflex mang lại sự linh hoạt tối đa. Với trọng lượng siêu nhẹ chỉ 77g và thiết kế đối xứng nhỏ gọn, chiếc chuột này mang lại cảm giác cầm nắm thoải mái, kiểm soát mượt mà, phù hợp cho những trận đấu kéo dài. Tùy chỉnh trải nghiệm của bạn với 10 nút có thể lập trình và ánh sáng Razer Chroma RGB sống động, biến mỗi cú nhấp chuột thành một màn trình diễn ánh sáng đầy mê hoặc. Chuột Razer Cobra Pro Trắng không chỉ là một vũ khí, mà là người bạn đồng hành đáng tin cậy, giúp bạn bứt phá mọi giới hạn và tiến tới chiến thắng.',
        1, 'https://product.hstatic.net/200000722513/product/42rc3efe_13d1959281ec44749394fad1ceebece4.png'
    );
