INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Chuột không dây Corsair Darkstar RGB (CH-931A011-AP)', 'chuot-khong-day-corsair-darkstar-rgb-ch-931a011-ap', 5,
        '{"Kết nối": "Không dây / Bluetooth", "Thời lượng pin": "Không dây: Lên đến 65 giờ, Bluetooth: Lên đến 80 giờ", "Led": "RGB", "Cảm biến": "Marksman 26K", "DPI": "26000", "Switch": "Optical"}',
        'Chuột không dây Corsair Darkstar RGB (CH-931A011-AP) là một lựa chọn hoàn hảo dành cho các game thủ chuyên nghiệp và những ai yêu thích sự linh hoạt cùng hiệu suất đỉnh cao. Với thiết kế công thái học vượt trội, Darkstar RGB đảm bảo sự thoải mái tối đa ngay cả trong những phiên chơi game kéo dài, ôm vừa vặn trong lòng bàn tay, giảm thiểu mệt mỏi. Được trang bị cảm biến quang học Corsair Marksman tiên tiến với độ nhạy lên đến 26.000 DPI, chuột mang lại độ chính xác gần như tuyệt đối, cho phép mọi cử động, mọi cú click đều được ghi nhận một cách hoàn hảo. Tần số phản hồi siêu nhanh 2.000Hz qua công nghệ Slipstream Wireless độc quyền của Corsair đảm bảo độ trễ gần như bằng không, mang lại trải nghiệm không dây mượt mà như chuột có dây. Ngoài ra, Darkstar RGB còn tích hợp 11 nút lập trình được, cho phép người dùng tùy chỉnh macro, gán các lệnh phức tạp hoặc điều khiển media một cách dễ dàng thông qua phần mềm iCUE mạnh mẽ của Corsair. Hệ thống đèn nền RGB động với 5 vùng chiếu sáng riêng biệt có thể đồng bộ hóa với các thiết bị Corsair khác, tạo nên một không gian chơi game đầy màu sắc và cá tính. Với thời lượng pin ấn tượng lên đến 80 giờ khi kết nối qua Bluetooth hoặc 65 giờ qua Slipstream Wireless (tắt đèn RGB), Darkstar RGB cho phép bạn chiến game không ngừng nghỉ. Chuột cũng hỗ trợ sạc nhanh qua cổng USB-C, giúp bạn nhanh chóng trở lại cuộc chơi. Trọng lượng nhẹ cùng feet chuột PTFE ma sát thấp giúp lướt mượt mà trên mọi bề mặt, biến Corsair Darkstar RGB thành vũ khí tối thượng, đưa bạn đến chiến thắng trong mọi trận chiến.',
        1, 'https://product.hstatic.net/200000722513/product/zkcjs5el_4e9208da4d9648fba24bf590fed636cd.png'
    );
