INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC 24G11ZE 24" Fast IPS 240Hz chuyên game', 'man-hinh-aoc-24g11ze-24-fast-ips-240hz-chuyen-game', 54,
        '{"Nhà sản xuất": "AOC"}',
        'Màn hình AOC 24G11ZE 24\" Fast IPS 240Hz chuyên game là lựa chọn hoàn hảo cho những game thủ tìm kiếm trải nghiệm đỉnh cao và lợi thế cạnh tranh vượt trội. Với tấm nền Fast IPS tiên tiến, màn hình này không chỉ mang đến màu sắc sống động, độ tương phản ấn tượng và góc nhìn rộng, mà còn đảm bảo tốc độ phản hồi cực nhanh, thường là 1ms GtG, loại bỏ hiện tượng bóng ma và nhòe hình trong các pha hành động tốc độ cao. Tần số quét 240Hz siêu mượt là điểm nhấn không thể bỏ qua, giúp mọi chuyển động trong game trở nên trôi chảy đến khó tin, cho phép bạn phản ứng nhanh hơn và nắm bắt mọi khoảnh khắc quan trọng. Kích thước 24 inch là lý tưởng cho thi đấu chuyên nghiệp, vừa vặn tầm nhìn mà không cần phải đảo mắt quá nhiều. Độ phân giải Full HD (1920x1080) tối ưu hóa hiệu suất, đảm bảo card đồ họa của bạn có thể đạt được khung hình cao nhất. Sản phẩm còn tích hợp công nghệ Adaptive Sync (FreeSync Premium) để đồng bộ hóa tần số quét của màn hình với GPU, loại bỏ hoàn toàn hiện tượng xé hình và giật lag, mang lại trải nghiệm chơi game mượt mà không gián đoạn. Thiết kế viền mỏng ba cạnh mang lại không gian hiển thị rộng rãi và phù hợp cho thiết lập đa màn hình. Các tính năng hỗ trợ game thủ như Crosshair Overlay, chế độ tùy chỉnh game (FPS, RTS), và công nghệ bảo vệ mắt Flicker-Free cùng Low Blue Light giúp bạn thoải mái chiến đấu hàng giờ liền mà không lo mỏi mắt. Cổng kết nối đa dạng bao gồm DisplayPort và HDMI, đảm bảo khả năng tương thích rộng rãi với nhiều thiết bị khác nhau. Đây chắc chắn là công cụ đắc lực để bạn vươn tới chiến thắng.',
        1, 'https://cdn.hstatic.net/products/200000722513/aoc_24g11ze_gearvn_833138fa3d134dbc8a02a337198c99d5.jpg'
    );
