INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ViewSonic ColorPro VP2456A 24" IPS 120Hz USBC chuyên đồ hoạ', 'man-hinh-viewsonic-colorpro-vp2456a-24-ips-120hz-usbc-chuyen-do-hoa', 55,
        '{"Nhà sản xuất": "ViewSonic"}',
        'Màn hình ViewSonic ColorPro VP2456A 24\" IPS 120Hz USB-C là lựa chọn lý tưởng cho các chuyên gia đồ họa, nhiếp ảnh và biên tập video đòi hỏi độ chính xác màu sắc tuyệt đối. Với tấm nền IPS cao cấp, màn hình này mang lại góc nhìn rộng và tái tạo màu sắc sống động, chân thực, đảm bảo rằng mọi chi tiết trong tác phẩm của bạn được hiển thị một cách trung thực nhất. Công nghệ ColorPro đảm bảo độ chính xác màu Delta E < 2, cùng với việc được hiệu chỉnh trước tại nhà máy, mang đến khả năng hiển thị màu sắc vượt trội ngay khi xuất xưởng. Tần số quét 120Hz không chỉ giúp trải nghiệm làm việc mượt mà hơn, giảm mỏi mắt khi di chuyển chuột hay cuộn trang, mà còn nâng cao hiệu suất làm việc tổng thể. Đặc biệt, cổng USB-C đa năng cho phép kết nối một dây duy nhất để truyền tải dữ liệu, video và cấp nguồn cho laptop, giúp bàn làm việc gọn gàng và hiệu quả. Ngoài ra, màn hình còn tích hợp các cổng kết nối phổ biến như HDMI và DisplayPort, cùng hub USB để dễ dàng kết nối các thiết bị ngoại vi. Thiết kế công thái học với khả năng điều chỉnh độ cao, xoay, nghiêng và xoay trục (pivot) giúp người dùng dễ dàng tìm được tư thế làm việc thoải mái nhất trong thời gian dài.',
        1, 'https://cdn.hstatic.net/products/200000722513/view_vp2456a_gearvn_d787d732d11c44938e47df4263d7163b.jpg'
    );
