INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Viewsonic VA2432-H-2 24" IPS 100Hz viền mỏng', 'man-hinh-viewsonic-va2432-h-2-24-ips-100hz-vien-mong', 55,
        '{"Nhà sản xuất": "ViewSonic"}',
        'Màn hình Viewsonic VA2432-H-2 kích thước 24 inch là lựa chọn lý tưởng cho cả công việc và giải trí, mang lại trải nghiệm hình ảnh tuyệt vời. Được trang bị tấm nền IPS tiên tiến, màn hình này đảm bảo góc nhìn rộng 178 độ cả chiều ngang lẫn chiều dọc, giúp màu sắc và độ tương phản không bị biến đổi dù bạn nhìn từ bất kỳ vị trí nào. Độ phân giải Full HD (1920x1080) sắc nét cùng tần số quét 100Hz mượt mà là điểm cộng lớn, đặc biệt khi xem phim hành động hay chơi game nhẹ, giảm thiểu hiện tượng xé hình và giật lag, mang lại chuyển động liền mạch và chân thực hơn. Thiết kế viền mỏng (frameless) không chỉ tạo nên vẻ ngoài hiện đại, tinh tế mà còn tối ưu hóa không gian hiển thị, rất phù hợp cho thiết lập đa màn hình, cho phép chuyển đổi tầm nhìn mượt mà giữa các màn hình mà không bị gián đoạn. Công nghệ Flicker-Free và bộ lọc ánh sáng xanh tích hợp giúp bảo vệ mắt bạn khỏi mỏi mệt trong suốt quá trình sử dụng lâu dài, nâng cao sự thoải mái. Màn hình còn được trang bị đầy đủ các cổng kết nối phổ biến như HDMI và VGA, dễ dàng tương thích với nhiều thiết bị. Viewsonic VA2432-H-2 là sự kết hợp hoàn hảo giữa hiệu năng, thiết kế và công nghệ bảo vệ mắt, đáp ứng đa dạng nhu cầu của người dùng từ văn phòng đến giải trí gia đình.',
        1, 'https://cdn.hstatic.net/products/200000722513/view_va2432-h-2_gearvn_6c9b444681ce460d819fbbdb160d6c35.jpg'
    );
