INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ViewSonic VX2428AJ-BL 24 inch IPS 240Hz Gsync', 'man-hinh-viewsonic-vx2428aj-bl-24-ips-240hz-gsync-chuyen-game', 55,
        '{"Thương hiệu":"ViewSonic","Kích cỡ":"24 inch","Độ phân giải":"FHD (1920 x 1080)","Tỷ lệ khung hình":"16:9","Tấm nền":"IPS","Độ sáng màn hình":"250cd/m2","Độ tương phản":"1000:1","Kiểu màn hình":"Màn hình phẳng","Thời gian phản hồi":"0.5 ms","Tần số quét":"240Hz","Cổng kết nối":"1 x 3.5mm Audio Out, 1 x DisplayPort 1.2, 2 x HDMI 1.4, DC Socket","Phân loại màn hình":"Gaming","VESA":"100 x 100 mm","Kích thước (Có chân đế)":"541.86 x 417~547 x 207 mm","Kích thước (Không có chân đế)":"541.86 x 323 x 43.5 mm","Trọng lượng (Có chân đế)":"3.8 kg","Trọng lượng (Không có chân đế)":"2.8 kg","Trọng lượng (Có bao bì)":"5.5 kg"}',
        'Màn hình ViewSonic VX2428AJ-BL là một sản phẩm màn hình ViewSonic không thể bỏ qua đối với những game thủ và người dùng yêu thích trải nghiệm hình ảnh sắc nét. Với kích thước 24 inch và công nghệ IPS, màn hình này hứa hẹn sẽ mang đến trải nghiệm chơi game và giải trí tuyệt vời. Hãy cùng khám phá các đặc tính và tính năng nổi bật của sản phẩm màn hình gaming này trong bài đánh giá chi tiết dưới đây.\n\nĐộ phân giải Full HD (1920 x 1080)\nMàn hình ViewSonic VX2428AJ-BL sở hữu độ phân giải Full HD 1920 x 1080, giúp hình ảnh sắc nét và chân thực. Độ phân giải này đặc biệt phù hợp với các trò chơi hiện đại và nội dung đa phương tiện, mang lại trải nghiệm hình ảnh sống động.\n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nTần số quét 240Hz\nĐiểm nhấn của màn hình là tần số quét 240Hz và thời gian phản hồi 0.5ms MPRT siêu nhanh. Tần số quét cao loại bỏ hiện tượng xé hình, giật lag, mang lại chuyển động siêu mượt mà trong các tựa game FPS hay MOBA. Thời gian phản hồi cực thấp đảm bảo mọi hình ảnh chuyển động đều sắc nét, không bị nhòe mờ, giúp game thủ phản ứng nhanh và chính xác.\n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nCông nghệ G-Sync\nĐể tối ưu hóa trải nghiệm, VX2428AJ-BL tích hợp AMD FreeSync™ Premium và tương thích NVIDIA G-Sync. Hai công nghệ này đồng bộ hóa tần số quét của màn hình với card đồ họa, loại bỏ hoàn toàn hiện tượng xé hình và giật hình. Nhờ đó, hình ảnh luôn mượt mà, liền mạch, cho phép bạn tập trung hoàn toàn vào trận đấu.\n\nMàn hình IPS\nMàn hình IPS cung cấp góc nhìn rộng lên đến 178 độ, cho phép bạn nhìn rõ hình ảnh mà không bị biến dạng màu sắc. Tính năng này là một điểm cộng lớn, đặc biệt khi bạn đang tham gia những trận đấu co-op hoặc chơi game cùng bạn bè.\n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nThiết kế thanh lịch và tiện dụng\nViewSonic VX2428AJ-BL được trang bị chân đế công thái học đa năng, cho phép điều chỉnh độ cao, xoay, nghiêng và xoay dọc, mang lại tư thế thoải mái nhất. Đặc biệt, các công nghệ Low Blue Light và Flicker-Free giúp bảo vệ đôi mắt của bạn khỏi mệt mỏi khi sử dụng trong thời gian dài. Đây là sự kết hợp hoàn hảo giữa hiệu năng và sự thoải mái. \n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nĐộ sáng và độ tương phản ấn tượng\nVới độ sáng tối đa là 250 nits và tỷ lệ độ tương phản 1000:1, VX2428AJ-BL mang đến những hình ảnh rõ nét và sống động ngay cả trong những điều kiện ánh sáng mạnh. Điều này giúp cải thiện trải nghiệm người dùng khi xem phim hay chơi game trong môi trường khác nhau.\n\nBảo vệ mắt\nMàn hình máy tính được trang bị công nghệ Flicker-Free và Blue Light Filter, giúp giảm thiểu mỏi mắt và bảo vệ sức khỏe mắt cho người dùng trong quá trình sử dụng lâu dài. Đây là một yếu tố quan trọng cho những game thủ hay những người làm việc văn phòng.\n\nMàn hình ViewSonic VX2428AJ-BL 24\n\nTính năng đa kết nối\nMàn hình cung cấp các cổng kết nối phong phú bao gồm hai HDMI 2.0 và một DisplayPort 1.4, đáp ứng mọi nhu cầu kết nối từ laptop, PC đến console. Cổng Audio Out 3.5mm và loa tích hợp cũng tăng cường tiện ích. Ngoài ra, thiết kế gọn gàng với khe khóa Kensington và quản lý cáp giúp không gian làm việc của bạn luôn ngăn nắp.',
        1, 'https://product.hstatic.net/200000722513/product/view_vx2428aj-bl_gearvn_a1a9750917fe420cb7d880b162d6a1e9.jpg'
    );
