INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC 24G4H 24" Fast IPS 200Hz chuyên game', 'man-hinh-aoc-24g4h-24-fast-ips-200hz-chuyen-game', 54,
        '{"Nhà sản xuất": "AOC"}',
        'Màn hình AOC 24G4H 24\" Fast IPS 200Hz chuyên game là lựa chọn hoàn hảo cho các game thủ đòi hỏi trải nghiệm hình ảnh mượt mà và sống động. Với kích thước 24 inch tiêu chuẩn, màn hình này tối ưu cho không gian làm việc và chơi game, đồng thời mang lại góc nhìn rộng. Tấm nền Fast IPS tiên tiến mang lại màu sắc rực rỡ, độ tương phản cao cùng góc nhìn rộng lên đến 178 độ, đảm bảo mọi chi tiết hiển thị sắc nét và chân thực từ mọi góc độ. Tần số quét 200Hz siêu nhanh kết hợp thời gian phản hồi cực thấp 1ms MPRT (Moving Picture Response Time) giúp loại bỏ hoàn toàn hiện tượng xé hình, giật hình và bóng ma chuyển động, mang đến lợi thế cạnh tranh vượt trội trong các tựa game hành động nhanh và eSports. Công nghệ AMD FreeSync Premium được tích hợp sẵn còn đồng bộ hóa tốc độ khung hình của màn hình với card đồ họa, đảm bảo trải nghiệm chơi game liền mạch không gián đoạn, mượt mà ở mọi mức khung hình. Thiết kế công thái học với chân đế đa năng cho phép điều chỉnh độ cao, xoay, nghiêng và pivot linh hoạt, tối ưu hóa tư thế ngồi và giảm mỏi mắt, cổ khi sử dụng trong thời gian dài. Viền màn hình siêu mỏng tạo không gian hiển thị rộng hơn, lý tưởng cho thiết lập đa màn hình, chìm đắm hoàn toàn vào thế giới game. AOC 24G4H không chỉ là công cụ giải trí mà còn là người bạn đồng hành đáng tin cậy trên mọi chiến trường ảo, giúp bạn nắm bắt mọi khoảnh khắc quyết định.',
        1, 'https://product.hstatic.net/200000722513/product/aoc_24g4h_200hz_gearvn_2e44669d656a4c688fc62379e991572e.jpg'
    );
