INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn Hình Samsung LS24F320GAEXXV 24" IPS 120Hz', 'man-hinh-samsung-ls24f320gaexxv-24-ips-120hz', 16,
        '{"Nhà sản xuất": "Samsung"}',
        'Màn Hình Samsung LS24F320GAEXXV 24\" IPS 120Hz là sự lựa chọn lý tưởng cho những ai tìm kiếm trải nghiệm hình ảnh mượt mà và sống động, đáp ứng tốt cả nhu cầu làm việc, giải trí lẫn chơi game. Với kích thước 24 inch tiêu chuẩn, chiếc màn hình này mang lại không gian hiển thị rộng rãi mà vẫn gọn gàng trên bàn làm việc.\n\nĐiểm nhấn nổi bật chính là tấm nền IPS cao cấp, đảm bảo góc nhìn rộng lên đến 178 độ, giúp màu sắc và độ tương phản không bị biến đổi dù bạn nhìn từ bất kỳ vị trí nào. Công nghệ IPS còn mang lại khả năng tái tạo màu sắc chân thực, rực rỡ và chính xác, phù hợp cho các tác vụ đòi hỏi độ chuẩn màu cao như thiết kế đồ họa, chỉnh sửa ảnh hay xem phim chất lượng cao.\n\nKhông chỉ dừng lại ở chất lượng hình ảnh, Samsung LS24F320GAEXXV còn nâng tầm trải nghiệm mượt mà với tần số quét 120Hz. Đây là một cải tiến đáng kể so với các màn hình 60Hz truyền thống, giúp mọi chuyển động trên màn hình trở nên trơn tru và ít giật lag hơn đáng kể. Đối với game thủ, tần số quét 120Hz mang lại lợi thế rõ rệt trong các tựa game tốc độ cao, giảm thiểu hiện tượng xé hình và mờ nhòe, giúp bạn phản ứng nhanh hơn và đắm chìm vào thế giới ảo. Ngay cả khi sử dụng hàng ngày, việc cuộn trang web hay di chuyển cửa sổ cũng trở nên mượt mà, dễ chịu hơn nhiều.\n\nThiết kế của màn hình cũng được chú trọng với viền mỏng ba cạnh, tối ưu hóa không gian hiển thị và tạo vẻ ngoài hiện đại, sang trọng. Điều này không chỉ giúp tăng tính thẩm mỹ mà còn rất tiện lợi khi thiết lập đa màn hình, mang lại trải nghiệm liền mạch.\n\nMàn hình được trang bị các cổng kết nối phổ biến, đảm bảo khả năng tương thích với nhiều thiết bị khác nhau. Samsung LS24F320GAEXXV thực sự là một khoản đầu tư xứng đáng cho hiệu suất làm việc và giải trí tối ưu.',
        1, 'https://product.hstatic.net/200000722513/product/samsung_ls24f320gaexxv_gearvn_3f8ffeeddd364bab96e38d4ed1d0d9c0.jpg'
    );
