INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Acer KG240Y-X1 24" IPS 200Hz Gsync chuyên game', 'man-hinh-acer-kg240y-x1-24-ips-200hz-gsync-chuyen-game', 35,
        '{"Nhà sản xuất": "Acer"}',
        'Màn hình Acer KG240Y-X1 24\" IPS 200Hz Gsync là một chiến binh đích thực dành cho mọi game thủ, từ nghiệp dư đến chuyên nghiệp, mong muốn một trải nghiệm chơi game không giới hạn. Với kích thước 24 inch tiêu chuẩn, màn hình này cung cấp không gian hiển thị tối ưu, đủ rộng để đắm chìm nhưng vẫn giữ được sự tập trung cao độ vào mọi hành động trên màn hình. Tấm nền IPS cao cấp là trái tim của KG240Y-X1, mang đến khả năng tái tạo màu sắc vượt trội, sống động như thật với góc nhìn siêu rộng 178 độ, đảm bảo hình ảnh luôn sắc nét và chân thực dù bạn nhìn từ bất kỳ vị trí nào. Điểm nhấn không thể bỏ qua là tần số quét cực khủng 200Hz, giúp mọi chuyển động trong game diễn ra mượt mà đến kinh ngạc, loại bỏ hoàn toàn hiện tượng giật lag hay bóng mờ (ghosting), mang lại lợi thế quyết định trong các tựa game tốc độ cao như FPS, MOBA hay đua xe. Kết hợp với công nghệ G-Sync Compatible, màn hình này đồng bộ hóa hoàn hảo với card đồ họa NVIDIA của bạn, loại bỏ triệt để hiện tượng xé hình (tearing) và giảm thiểu độ trễ đầu vào, cho phép bạn phản ứng nhanh nhạy và chính xác hơn bao giờ hết. Thời gian phản hồi siêu nhanh (thường là 0.5ms hoặc 1ms VRB trên các mẫu gaming cao cấp) càng củng cố vị thế của KG240Y-X1 như một công cụ chơi game đỉnh cao. Thiết kế viền mỏng ZeroFrame không chỉ tạo tính thẩm mỹ mà còn lý tưởng cho thiết lập đa màn hình, mở rộng không gian trải nghiệm. Acer KG240Y-X1 không chỉ là một chiếc màn hình, mà là một vũ khí giúp bạn chinh phục mọi thử thách trong thế giới ảo.',
        1, 'https://product.hstatic.net/200000722513/product/acer_kg240y_x1_gearvn_a8aad1a4eb7c460ea9cacf5aecc2b15f.jpg'
    );
