INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ViewSonic XG2409A 24" IPS 240Hz 1ms chuyên game', 'man-hinh-viewsonic-xg2409a-24-ips-240hz-1ms-chuyen-game', 55,
        '{"Nhà sản xuất": "ViewSonic"}',
        'Màn hình ViewSonic XG2409A là sự lựa chọn hoàn hảo dành cho các game thủ chuyên nghiệp và những ai đam mê trải nghiệm gaming đỉnh cao. Với kích thước 24 inch, sản phẩm mang lại không gian hiển thị vừa phải, tối ưu cho các trận đấu căng thẳng. Điểm nhấn vượt trội của XG2409A nằm ở tấm nền IPS cao cấp, không chỉ đảm bảo góc nhìn rộng 178 độ mà còn tái tạo màu sắc sống động và chân thực đến bất ngờ, giúp bạn đắm chìm hoàn toàn vào thế giới ảo. Tốc độ là yếu tố then chốt trong mọi cuộc chiến, và ViewSonic XG2409A không làm bạn thất vọng với tần số quét 240Hz siêu tốc. Điều này đồng nghĩa với việc mọi chuyển động trên màn hình đều mượt mà một cách kinh ngạc, loại bỏ hoàn toàn hiện tượng xé hình hay giật lag, mang lại lợi thế quyết định trong các tựa game FPS hoặc MOBA yêu cầu phản xạ nhanh. Kết hợp với thời gian phản hồi chỉ 1ms (MPRT), màn hình này giúp loại bỏ hoàn toàn hiện tượng bóng mờ (ghosting), đảm bảo hình ảnh luôn sắc nét dù cho hành động có nhanh đến mấy. Ngoài ra, màn hình còn được trang bị công nghệ đồng bộ hóa hình ảnh tiên tiến (ví dụ: FreeSync Premium), giúp loại bỏ triệt để hiện tượng xé hình và giật hình bằng cách đồng bộ hóa tần số quét của màn hình với card đồ họa của bạn. Thiết kế công thái học cho phép điều chỉnh độ cao, xoay, nghiêng linh hoạt, tối ưu hóa tư thế ngồi chơi game, giảm thiểu mệt mỏi trong thời gian dài. Với ViewSonic XG2409A, bạn không chỉ sở hữu một công cụ chơi game mạnh mẽ mà còn là một trung tâm giải trí đa phương tiện cao cấp.',
        1, 'https://product.hstatic.net/200000722513/product/view_xg2409a_gearvn_1b136568d145494c9871559bbe0e0ca0.jpg'
    );
