INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ASUS VY249HGR 24" IPS 120Hz viền mỏng', 'man-hinh-asus-vy249hgr-24-ips-120hz-vien-mong', 3,
        '{"Kích thước màn hình": "24 inches", "Kích thước hiển thị": "23.8 inch", "Kiểu màn hình": "Màn hình phẳng", "Tấm nền": "IPS", "Tỉ lệ màn hình": "16:9", "Độ phân giải": "1920 x 1080 pixels", "Tần số quét": "120 Hz", "Thời gian phản hồi": "1 ms", "Số lượng màu": "16.7 triệu màu", "Độ sáng": "250 cd/㎡", "Góc nhìn": "178°/178°", "Độ tương phản tĩnh": "1500:1", "Công nghệ màn hình": "Trace Free, SPLENDID, 4 chế độ nhiệt độ màu, GamePlus, QuickFit, HDCP 1.4, Adaptive-Sync, GameFast Input, Shadow Boost, Motion Sync, Eye Care+", "Nghiêng": "+23° ~ -5°", "Điều chỉnh độ cao": "Không", "Treo tường": "100 x 100 mm", "Cổng kết nối": "1x HDMI v1.4, 1x VGA, Earphone Jack", "Tính năng màn hình": "Hạn chế ánh sáng xanh, Chống chói, Chế độ chơi game, Chống nháy, Chế độ tiết kiệm điện", "Công suất tiêu thụ": "<15W, Chế độ tiết kiệm: <0.5W, Chế độ tắt: <0.3W", "Kích thước": "Có chân đế: 54.1 x 39.5 x 18.5 cm, Không có chân đế: 54.1 x 32.3 x 4.8 cm", "Trọng lượng": "Có chân đế: 3.4 kg, Không có chân đế: 2.9 kg"}',
        'Màn hình ASUS VY249HGR 120HZ 24 inch là trợ thủ đắc lực cho dàn PC thêm phần chuyên nghiệp, hiệu quả tối ưu. Sở hữu nhiều đặc điểm ưu trội, mẫu màn hình ASUS này hứa hẹn thỏa mãn thị giác người dùng trong mọi hoạt động.\n\nPhân giải cao, chuyển động mượt\nMàn hình ASUS VY249HGR 120HZ 24 inch nổi bật với phân giải Full HD, hiển thị chi tiết và rõ ràng trên kích thước 23,8 inch. Độ phân giải cao giúp các chi tiết sắc nét và tăng cường trải nghiệm cho người dùng ở đa dạng nhu cầu. \n\nChất lượng hiển thị của màn hình ASUS VY249HGR 120HZ 24 inch\n\nĐiểm đáng chú ý của phiên bản là tần số quét 120Hz, cung cấp chuyển động mượt mà, ít bị giật lag hay xé hình. Nhờ tốc độ làm mới nhanh, màn hình có khả năng bắt kịp các chuyển động nhanh, tối ưu hóa phản xạ trong các trò chơi hành động. Tần số quét cao kết hợp với độ trễ thấp MPRT 1ms cũng giúp người dùng có được cảm giác chính xác khi thao tác và xử lý nhanh chóng các tình huống.\n\nThiết kế bền bỉ, màu năng động\nMàn hình ASUS VY249HGR 120HZ 24 inch gây ấn tượng với phong cách hiện đại, khung viền mỏng nhằm tăng diện tích hiển thị và tạo cảm giác liền mạch. Bên cạnh đó, thiết kế của màn hình này khá bền bỉ và phù hợp với nhu cầu sử dụng lâu dài, hạn chế trầy xước, giảm thiểu hư hỏng tối ưu. \n\nThiết kế màn hình ASUS VY249HGR 120HZ 24 inch\n\nVới màu sắc đen cơ bản, tổng thể màn hình tương đối trẻ trung, phù hợp với nhiều loại không gian. Ngoài ra, hãng cũng tập trung vào tính tiện dụng của phiên bản khi tối giản chi tiết thừa nhưng vẫn giữ được vẻ ngoài tinh tế. Kèm theo chân đế chắc chắn, có thể tùy chỉnh linh hoạt góc nhìn, mang đến góc sử dụng thoải mái cho người dùng.\n\nTấm nền IPS, công nghệ Game Plus \nMàn hình ASUS VY249HGR 120HZ 24 inch sử dụng tấm nền IPS cao cấp, nổi bật với khả năng hiển thị màu sắc chính xác và độ tương phản tốt. Công nghệ này nổi tiếng với góc nhìn rộng lên đến 178 độ, với màu sắc và độ sáng đồng nhất kể cả khi nhiều người cùng xem hoặc khi chia sẻ màn hình với người khác. \n\nCông nghệ hiển thị, tiện ích thông minh đi kèm của màn hình ASUS VY249HGR 120HZ 24 inch\n\nHơn thế nữa, công nghệ Game Plus của màn hình còn tích hợp sẵn các tính năng đặc biệt hỗ trợ chơi game như chế độ tâm ngắm, hẹn giờ, bộ đếm FPS và tính năng điều chỉnh vị trí màn hình. Qua đó, các game thủ có thể tùy chỉnh màn hình để tối ưu hóa trải nghiệm, đồng thời có lợi thế cạnh tranh khi theo dõi và thực hiện các thao tác nhanh. \n\nKết nối mượt, hỗ trợ đa cổng\nMàn hình ASUS VY249HGR 120HZ 24 inch trang bị nhiều cổng giao tiếp, bao gồm cả HDMI và VGA phù hợp nhu cầu kết nối đa dạng. Theo đó, các cổng giao tiếp trên màn hình được bố trí thuận tiện, dễ dàng sử dụng mà không cần phải di chuyển màn hình hay thực hiện thao tác phức tạp. Việc hỗ trợ đa dạng các cổng kết nối cũng giúp màn hình dễ dàng tích hợp vào hệ thống hiện có của người dùng. \n\nHệ thống cổng kết nối của màn hình ASUS VY249HGR 120HZ 24 inch\n\nĐồng thời cho phép chuyển đổi nhanh chóng giữa các thiết bị mà không gặp rắc rối về khả năng tương thích. Mặt khác, màn hình này còn hỗ trợ người dùng sử dụng thêm các thiết bị ngoại vi, từ đó hoàn thiện trải nghiệm chơi game một cách toàn diện.',
        1, 'https://product.hstatic.net/200000722513/product/asus_vy249hgr_120hz_gearvn_3b944fdc15e449968d9ba0739aaea352.jpg'
    );
