INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Dahua DHI-LM24-A200Y 24" 100Hz', 'man-hinh-dahua-dhi-lm24-a200y-24-100hz', 57,
        '{"Nhà sản xuất": "DAHUA"}',
        'Màn hình Dahua DHI-LM24-A200Y 24\" 100Hz là lựa chọn lý tưởng cho người dùng tìm kiếm một thiết bị hiển thị cân bằng giữa hiệu suất và giá trị. Với kích thước 24 inch tiêu chuẩn, màn hình này mang lại không gian làm việc và giải trí thoải mái, không quá cồng kềnh nhưng vẫn đủ rộng rãi để đa nhiệm. Độ phân giải Full HD (1920x1080) sắc nét đảm bảo mọi hình ảnh, văn bản và video đều được hiển thị chi tiết, sống động, phục vụ tốt cho cả công việc văn phòng, duyệt web lẫn xem phim.\n\nĐiểm nổi bật của DHI-LM24-A200Y là tần số quét 100Hz. Mức tần số này cao hơn đáng kể so với các màn hình 60Hz truyền thống, mang lại trải nghiệm hình ảnh mượt mà hơn, đặc biệt rõ rệt khi cuộn trang, di chuyển chuột hoặc trong các cảnh chuyển động nhanh của trò chơi và phim ảnh. Điều này giúp giảm thiểu hiện tượng giật hình và bóng mờ, tạo cảm giác dễ chịu cho mắt khi sử dụng trong thời gian dài. Thời gian phản hồi nhanh (thường là 5ms GtG hoặc thấp hơn) càng củng cố khả năng hiển thị mượt mà, hạn chế tối đa độ trễ hình ảnh.\n\nMàn hình được trang bị tấm nền chất lượng cao (thường là IPS hoặc VA), cung cấp góc nhìn rộng lên đến 178 độ cả chiều ngang và dọc, đảm bảo màu sắc và độ sáng không bị biến đổi dù bạn nhìn từ bất kỳ vị trí nào. Công nghệ chống nhấp nháy (Flicker-Free) và chế độ lọc ánh sáng xanh thấp (Low Blue Light) tích hợp giúp bảo vệ thị lực người dùng, giảm mỏi mắt khi làm việc liên tục trước màn hình.\n\nThiết kế viền mỏng ba cạnh mang lại vẻ ngoài hiện đại, tinh tế và tối ưu không gian hiển thị, rất phù hợp cho thiết lập đa màn hình. Các cổng kết nối đa dạng như HDMI và VGA (hoặc DisplayPort) cung cấp khả năng tương thích rộng rãi với nhiều thiết bị khác nhau, từ máy tính để bàn, laptop cho đến các thiết bị giải trí.\n\nVới tất cả những tính năng này, Dahua DHI-LM24-A200Y là một lựa chọn tuyệt vời cho cả công việc và giải trí hàng ngày.',
        1, 'https://product.hstatic.net/200000722513/product/dahua_gearvn_78eaee7ab3fd4fb89aa7796875352b88.jpg'
    );
