INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Acer EK251Q G 25" IPS 120Hz', 'man-hinh-acer-ek251q-g-25-ips-120hz', 35,
        '{"Nhà sản xuất": "Acer"}',
        'Màn hình Acer EK251Q G với kích thước 25 inch là lựa chọn lý tưởng cho cả công việc, giải trí và chơi game nhẹ. Được trang bị tấm nền IPS cao cấp, màn hình này mang lại góc nhìn rộng lên đến 178 độ, cùng khả năng tái tạo màu sắc chân thực và sống động. Dù bạn đang xem phim, chỉnh sửa ảnh hay làm việc với các bảng tính, hình ảnh hiển thị luôn rõ ràng và sắc nét.\n\nĐiểm nổi bật của Acer EK251Q G chính là tần số quét 120Hz. So với các màn hình 60Hz truyền thống, tần số quét 120Hz giúp giảm thiểu hiện tượng xé hình (tearing) và giật lag, mang lại trải nghiệm mượt mà hơn đáng kể trong các tựa game tốc độ hay khi cuộn trang web nhanh. Điều này đặc biệt hữu ích cho những game thủ muốn có lợi thế trong các trò chơi eSports hoặc chỉ đơn giản là mong muốn một trải nghiệm hình ảnh trôi chảy hơn.\n\nKích thước 25 inch cung cấp không gian hiển thị rộng rãi mà vẫn giữ được mật độ điểm ảnh tốt, đảm bảo chi tiết hình ảnh không bị vỡ. Thiết kế viền mỏng ba cạnh giúp tối ưu hóa không gian hiển thị và tạo vẻ ngoài hiện đại, thanh lịch, dễ dàng bố trí trên mọi không gian làm việc hoặc giải trí. Các cổng kết nối đa dạng như HDMI và VGA cho phép người dùng dễ dàng kết nối với nhiều thiết bị khác nhau, từ PC, laptop cho đến các console chơi game. Acer EK251Q G là sự kết hợp hoàn hảo giữa hiệu năng ổn định, chất lượng hình ảnh sắc nét và giá thành phải chăng.',
        1, 'https://product.hstatic.net/200000722513/product/acer_ek251q_g_gearvn_85e3f9993e494b3f84b9999c1024f6ef.jpg'
    );
