INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình E-DRA EGM24F100H 24" IPS 100Hz', 'man-hinh-e-dra-egm24f100h-24-ips-100hz', 23,
        '{"Nhà sản xuất": "EDRA"}',
        'Màn hình E-DRA EGM24F100H là một lựa chọn tuyệt vời cho người dùng tìm kiếm sự cân bằng giữa hiệu năng làm việc và giải trí với mức giá phải chăng. Với kích thước 24 inch tiêu chuẩn, sản phẩm này mang lại không gian hiển thị vừa đủ rộng rãi mà không chiếm quá nhiều diện tích bàn làm việc. Điểm nổi bật nhất của EGM24F100H là tấm nền IPS cao cấp, cung cấp màu sắc sống động, chân thực và góc nhìn rộng 178 độ, đảm bảo hình ảnh không bị biến đổi dù bạn nhìn từ bất kỳ góc độ nào. Điều này đặc biệt hữu ích cho các công việc đòi hỏi độ chính xác màu sắc hoặc khi xem phim, chơi game cùng bạn bè. Độ phân giải Full HD (1920x1080) sắc nét hiển thị mọi chi tiết rõ ràng, từ văn bản, bảng tính cho đến các tựa game đồ họa phức tạp. Tần số quét 100Hz là một nâng cấp đáng giá so với các màn hình 60Hz truyền thống, mang lại trải nghiệm hình ảnh mượt mà hơn đáng kể, giảm thiểu hiện tượng xé hình (tearing) và giật lag (stuttering) trong các cảnh chuyển động nhanh. Điều này giúp game thủ có lợi thế phản ứng nhanh hơn trong các tựa game hành động và mang lại cảm giác dễ chịu hơn khi cuộn trang hay làm việc hàng ngày. Màn hình cũng được trang bị các cổng kết nối phổ biến như HDMI và DisplayPort, tương thích dễ dàng với nhiều thiết bị. Thiết kế viền mỏng ba cạnh không chỉ tạo vẻ ngoài hiện đại, tinh tế mà còn tối ưu không gian hiển thị, lý tưởng cho việc thiết lập đa màn hình. Ngoài ra, E-DRA EGM24F100H còn tích hợp các công nghệ bảo vệ mắt như Low Blue Light và Flicker-Free, giúp giảm mỏi mắt khi sử dụng trong thời gian dài, đảm bảo sức khỏe thị giác cho người dùng. Đây là một chiếc màn hình đa năng, phù hợp cho cả nhu cầu làm việc, học tập và giải trí.',
        1, 'https://product.hstatic.net/200000722513/product/edra_egm24f100h_gearvn_2c67262ed2cc428f9f76f9dab6c6b242.jpg'
    );
