INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ASUS ROG Strix XG259CMS 25" Fast IPS 310Hz 1ms G-Sync', 'man-hinh-asus-rog-strix-xg259cms-25-fast-ips-310hz-1ms-g-sync', 3,
        '{"Nhà sản xuất": "ASUS"}',
        'Màn hình ASUS ROG Strix XG259CMS 25\" Fast IPS 310Hz 1ms G-Sync là một kiệt tác công nghệ dành cho game thủ chuyên nghiệp và những ai khao khát trải nghiệm gaming đỉnh cao. Với kích thước 25 inch, màn hình này cung cấp không gian hiển thị lý tưởng để tập trung hoàn toàn vào trận đấu mà không bị phân tâm. Tấm nền Fast IPS tiên tiến mang lại màu sắc sống động, độ tương phản tuyệt vời cùng góc nhìn rộng, đảm bảo mọi chi tiết trên màn hình đều sắc nét và trung thực. Điểm nhấn vượt trội là tần số quét siêu tốc 310Hz, giúp loại bỏ hoàn toàn hiện tượng xé hình và giật lag, mang đến hình ảnh mượt mà đến khó tin ngay cả trong những pha hành động nhanh nhất. Kết hợp với thời gian phản hồi cực nhanh 1ms (GTG), XG259CMS đảm bảo mọi thao tác của bạn được thực hiện tức thì, tạo lợi thế quyết định trong các tựa game Esport. Công nghệ G-Sync giúp đồng bộ hóa tần số quét của màn hình với card đồ họa NVIDIA, loại bỏ hiện tượng giật hình và xé hình, đảm bảo trải nghiệm chơi game liền mạch và chân thực nhất. Thiết kế ROG Strix mạnh mẽ, cùng với các tính năng công thái học tiên tiến như điều chỉnh độ cao, xoay, nghiêng và trục đứng, cho phép bạn tìm được vị trí xem thoải mái nhất. Các cổng kết nối đa dạng cũng giúp dễ dàng kết nối với các thiết bị khác. Đây thực sự là lựa chọn hoàn hảo để nâng tầm đẳng cấp gaming của bạn.',
        1, 'https://product.hstatic.net/200000722513/product/asus_xg259cms_gearvn_6c2308acee8a467d96ea13b8ac20c5f8.jpg'
    );
