INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC 24B3CA2 24" IPS 100Hz USBC', 'man-hinh-aoc-24b3ca2-24-ips-100hz-usbc', 54,
        '{"Kích thước màn hình": "24 inches", "Kiểu màn hình": "Màn hình phẳng", "Độ phân giải": "1920 x 1080", "Tấm nền": "IPS", "Tỉ lệ màn hình": "16:9", "Tần số quét": "100 Hz", "Thời gian phản hồi": "1 ms", "Số lượng màu": "16.7 triệu", "Độ phủ màu": "NTSC 89% / sRGB 102%", "Độ sáng": "250 cd/m²", "Góc nhìn": "178°/178°", "Độ tương phản tĩnh": "1300:1", "Độ tương phản động": "20 Million:1", "Công nghệ màn hình": "Adaptive Sync, Flicker-Free giảm nhấp nháy", "Nghiêng": "-5° ~ 23°", "Treo tường": "100 x 100 mm", "Cổng kết nối": "1x HDMI 1.4, 1x USB-C (DP Alt, 65W), 2x USB3.2 Gen1, 1x Earphone", "Hệ thống âm thanh": "2W x 2", "Tính năng màn hình": "Hạn chế ánh sáng xanh, Chống chói, Chống nháy", "Công suất tiêu thụ": "30W", "Kích thước": "Có chân đế: 412.1 x 542.4 x 202.3 mm, Không chân đế: 316.2 x 542.4 x 44.6 mm", "Trọng lượng": "Có chân đế: 3.36 kg, Không chân đế: 3.04 kg"}',
        'Trong thế giới sống động của công nghệ hiện đại, màn hình không chỉ là một phần của không gian làm việc, mà còn là cánh cửa mở ra thế giới của sự sáng tạo và khám phá. Và giữa vô vàn lựa chọn trên thị trường, màn hình AOC 24B3CA2 nổi bật như một ngôi sao sáng giữa bầu trời đêm, đem lại cho bạn một trải nghiệm hiển thị vượt trội và đặc biệt.\n\nHiển thị vượt trội \nVới màn hình máy tính IPS rộng lớn 24 inch và góc nhìn mở rộng, AOC 24B3CA2 không chỉ là nơi bạn trải nghiệm mỗi khoảnh khắc với độ sắc nét và chi tiết tuyệt vời, mà còn là điểm hẹn của sự thăng hoa sáng tạo và công việc hiệu quả. Mọi đường nét, màu sắc và chi tiết đều được tái hiện một cách chân thực và cuốn hút, giúp bạn khám phá và phát triển ý tưởng mới một cách dễ dàng.  \n\nMàn hình AOC 24B3CA2 24\n\n \n\nTrải nghiệm mượt mà \nTần số 100Hz độc đáo giúp giảm thiểu hiện tượng xé hình, tạo nên một khung hình mượt mà, không gây mệt mỏi cho đôi mắt, từ đó mang lại trải nghiệm thú vị và ấn tượng. Từng hình ảnh trên màn hình AOC 24B3CA2 đều được tái hiện một cách mượt mà và chân thực như cuộc sống hiện thực, giúp giảm thiểu hiện tượng xé hình và mang lại trải nghiệm đầy ấn tượng. Không gây mỏi mắt, từ đó mang lại trải nghiệm thú vị và ấn tượng. \n\nMàn hình AOC 24B3CA2 24\n\nSắc màu sống động\nVới khả năng hiển thị 101% gam màu RGB và tỷ lệ tương phản 1300:1, mọi hình ảnh trở nên sống động và sắc nét, từ những tông màu nhẹ nhàng đến những đối tượng đậm nét, đều được tái hiện chân thực và cuốn hút trong chiếc màn hình AOC 24B3CA2.\n\nMàn hình AOC 24B3CA2 24\n\nKết nối tiện lợi\nVới cổng kết nối USB-C đa dụng và thiết kế linh hoạt VESA mountable, AOC 24B3CA2 không chỉ mang lại trải nghiệm hiển thị tối ưu, mà còn tạo điều kiện thuận lợi cho mọi hoạt động làm việc và giải trí của bạn. \n\nMàn hình AOC 24B3CA2 24\n\nCông nghệ giảm ánh sáng xanh\nLuôn quan râm đến sức khỏe người dùng, phải tiếp xúc màn hình nhiều nên đã trang bị công nghệ AOC Low Blue Mode giúp giảm lượng ánh sáng xanh. Đây là nguyên nhân chính dẫn đến tình trạng mỏi mắt đau mắt.',
        1, 'https://product.hstatic.net/200000722513/product/24b3ca2_f_f4157d055ccd4603ab095091bf87f6a7.png'
    );
