INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Dell UltraSharp U2424H 24" IPS 120Hz', 'man-hinh-dell-ultrasharp-u2424h-24-ips-120hz', 6,
        '{"Tên sản phẩm":"Màn hình Dell UltraSharp U2424H 24 inch IPS 120Hz","Kích thước":"24 inch","Kiểu màn hình":"Phẳng","Tấm nền":"IPS","Tỉ lệ khung hình":"16:9","Độ phân giải":"1920 x 1080 pixels","Tần số quét":"120 Hz","Thời gian phản hồi":"5 ms","Độ sáng":"250 cd/m²","Độ tương phản":"1000:1","Độ phủ màu sRGB":"100%","Góc nhìn":"178°","Góc nghiêng":"Có hỗ trợ","Điều chỉnh độ cao":"150 mm","Treo tường":"100 x 100 mm","Cổng kết nối":"DisplayPort 1.4 x 2, HDMI 1.4 x 1, Type-C x 2, USB A x 2, Cổng ra âm thanh","Hệ thống âm thanh":"Không","Tính năng màn hình":"Hạn chế ánh sáng xanh, Chống chói, Chống nháy, Chế độ tiết kiệm điện","Công suất tiêu thụ":"Thông thường 13.7W, Tối đa 72W"}',
        'Màn hình Dell UltraSharp U2424H 24\" IPS 120Hz là một lựa chọn lý tưởng cho các chuyên gia và người dùng yêu cầu chất lượng hình ảnh vượt trội cùng hiệu suất mượt mà. Với kích thước 24 inch, màn hình này cung cấp không gian làm việc tối ưu mà vẫn giữ được mật độ điểm ảnh sắc nét. Tấm nền IPS tiên tiến đảm bảo góc nhìn rộng 178 độ và tái tạo màu sắc sống động, chính xác đến từng chi tiết, rất phù hợp cho công việc thiết kế đồ họa, chỉnh sửa ảnh hoặc các tác vụ yêu cầu độ trung thực màu cao. Điểm nổi bật của U2424H là tần số quét 120Hz, mang lại trải nghiệm hình ảnh cực kỳ mượt mà, giảm hiện tượng xé hình và giật lag, không chỉ cải thiện sự thoải mái khi làm việc với các ứng dụng có chuyển động nhanh mà còn nâng cao trải nghiệm giải trí hoặc chơi game nhẹ.\n\nThiết kế của Dell UltraSharp U2424H thừa hưởng sự tinh tế đặc trưng của dòng UltraSharp, với viền màn hình siêu mỏng cho phép thiết lập đa màn hình liền mạch. Chân đế công thái học đa năng cho phép điều chỉnh độ cao, xoay, nghiêng và xoay dọc (pivot), giúp người dùng dễ dàng tìm được tư thế làm việc thoải mái nhất, giảm mỏi mắt và cổ trong thời gian dài.\n\nVề khả năng kết nối, màn hình này được trang bị đầy đủ các cổng cần thiết như HDMI, DisplayPort và đặc biệt là USB-C với khả năng cung cấp điện (power delivery), cho phép truyền dữ liệu, video và sạc laptop chỉ bằng một sợi cáp duy nhất, tối ưu hóa không gian bàn làm việc. Ngoài ra, hub USB tích hợp cũng giúp mở rộng khả năng kết nối thiết bị ngoại vi.\n\nDell UltraSharp U2424H không chỉ là một công cụ làm việc hiệu quả mà còn là một trung tâm giải trí đa phương tiện chất lượng cao.',
        1, 'https://product.hstatic.net/200000722513/product/monitor-ultrasharp-u2424h-gy-gallery-2_4197c0dd87854f26a6f84276613c0a48.png'
    );
