INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ASUS Swift Pro PG248QP 24" 540Hz G-Sync chuyên game', 'man-hinh-asus-swift-pro-pg248qp-24-540hz-g-sync-chuyen-game', 3,
        '{"Nhà sản xuất": "ASUS"}',
        'Màn hình ASUS Swift Pro PG248QP 24\" 540Hz G-Sync chuyên game là đỉnh cao công nghệ dành cho các game thủ esports chuyên nghiệp và những ai đòi hỏi tốc độ phản hồi tuyệt đối. Với kích thước 24 inch lý tưởng, màn hình này cung cấp góc nhìn tối ưu, giúp game thủ dễ dàng bao quát toàn bộ hành động trên màn hình mà không cần phải di chuyển mắt quá nhiều, một lợi thế cực kỳ quan trọng trong các trận đấu gay cấn. Điểm nổi bật nhất chính là tần số quét siêu khủng 540Hz, loại bỏ hoàn toàn hiện tượng xé hình và giật lag, mang lại trải nghiệm chơi game mượt mà đến khó tin, từng khung hình được hiển thị rõ ràng và sắc nét tức thì. Công nghệ NVIDIA G-Sync tích hợp đảm bảo đồng bộ hóa hoàn hảo giữa GPU và màn hình, loại bỏ tearing và stuttering, cho phép bạn tận hưởng mọi khoảnh khắc với độ chính xác cao nhất. Cùng với thời gian phản hồi cực nhanh, màn hình PG248QP giúp bạn phản ứng nhanh hơn đối thủ, biến từng mili giây thành lợi thế quyết định. Thiết kế công thái học cho phép điều chỉnh đa dạng về độ cao, góc nghiêng, xoay và pivot, giúp người dùng dễ dàng tìm được tư thế thoải mái nhất trong những phiên chơi game dài. Đây không chỉ là một chiếc màn hình, mà là công cụ chiến thắng giúp bạn vươn tới đỉnh cao trong mọi tựa game cạnh tranh.',
        1, 'https://product.hstatic.net/200000722513/product/avatar_ed191c39ece34a2b8644a2024cbb4a3b.jpg'
    );
