INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Viewsonic VA2436-H-PN 24" IPS 100Hz viền mỏng', 'man-hinh-viewsonic-va2436-h-pn-24-ips-100hz-vien-mong', 55,
        '{"Thương hiệu": "ViewSonic", "Bảo hành": "36 tháng"}',
        'Bước vào thế giới hình ảnh sắc nét và mượt mà với Màn hình Viewsonic VA2436-H-PN 24\" IPS 100Hz viền mỏng – sự kết hợp hoàn hảo giữa hiệu năng và phong cách hiện đại. Với kích thước 24 inch lý tưởng cùng tấm nền IPS cao cấp, bạn sẽ được đắm chìm trong màu sắc sống động, độ tương phản tuyệt vời và góc nhìn rộng vượt trội, đảm bảo mọi chi tiết hiển thị chân thực dù bạn nhìn từ bất kỳ vị trí nào.\n\nĐiểm nhấn đặc biệt của VA2436-H-PN chính là tần số quét 100Hz mượt mà. Khác biệt rõ rệt so với màn hình 60Hz truyền thống, tần số quét này mang lại trải nghiệm xem đã mắt hơn hẳn, từ việc cuộn trang web, xem phim hành động cho đến chơi các tựa game yêu cầu tốc độ vừa phải. Mọi chuyển động đều trở nên trôi chảy, giảm thiểu hiện tượng giật hình và bóng mờ, giúp đôi mắt bạn thoải mái hơn khi làm việc hay giải trí trong thời gian dài.\n\nThiết kế viền siêu mỏng không chỉ tạo nên vẻ ngoài thanh lịch, hiện đại cho không gian làm việc của bạn mà còn tối đa hóa diện tích hiển thị, mang lại cảm giác chìm đắm không giới hạn. Đây cũng là lựa chọn tuyệt vời cho các thiết lập đa màn hình, cho phép chuyển tiếp hình ảnh liền mạch như một bức tranh lớn. Độ phân giải Full HD (1920x1080) sắc nét cùng công nghệ bảo vệ mắt Flicker-Free và Blue Light Filter của ViewSonic giúp giảm mỏi mắt, bảo vệ thị lực của bạn trong suốt quá trình sử dụng.\n\nCho dù bạn là người dùng văn phòng, nhà thiết kế đồ họa nghiệp dư hay một game thủ yêu thích sự mượt mà, Màn hình Viewsonic VA2436-H-PN 24\" IPS 100Hz viền mỏng chắc chắn sẽ nâng tầm trải nghiệm thị giác của bạn lên một tầm cao mới.',
        1, 'https://product.hstatic.net/200000722513/product/gpg-23-mon-va2436-h-pn-prdp_f01_l.png_7d72506c812242f4a2bed9b835c32593.jpg'
    );
