INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình LG 24QP500-B 24" IPS 2K 75Hz Freesync HDR10', 'man-hinh-lg-24qp500-b-24-ips-2k-75hz-freesync-hdr10', 12,
        '{"Thương hiệu": "LG", "Bảo hành": "24 tháng", "Kích thước": "24 inch", "Độ phân giải": "2560 x 1440", "Tấm nền": "IPS", "Tần số quét": "75Hz", "Thời gian phản hồi": "5ms", "Kiểu màn hình": "Phẳng", "Độ sáng": "300 cd/m2", "Góc nhìn": "178° (H) / 178° (V)", "Khả năng hiển thị màu sắc": "16.7 triệu màu, 99% sRGB", "Độ tương phản tĩnh": "1000:1", "Độ tương phản động": "100.000.000:1", "Cổng xuất hình": "2 x HDMI (v2.0), 1 x DisplayPort 1.2", "Khối lượng": "Cả chân đế: 4.0 kg, Không chân đế: 3.4 kg, Vận chuyển: 5.5 kg", "Tiêu thụ điện": "22W", "Kích thước chuẩn": "Cả chân đế (Rộng x Cao x Dày): 540.1 x 413.2 x 211.5 mm, Không chân đế (Rộng x Cao x Dày): 540.1 x 321.1 x 41.9 mm, Vận chuyển (Rộng x Cao x Dày): 611 x 391 x 165 mm", "Phụ kiện đi kèm": "Dây nguồn, Cáp DisplayPort (có thể có), Sách hướng dẫn, Cáp HDMI, Phiếu bảo hành"}',
        'Màn hình LG 24QP500-B 24\" IPS 2K 75Hz Freesync HDR10 là một lựa chọn tuyệt vời cho những ai đang tìm kiếm sự kết hợp hoàn hảo giữa chất lượng hình ảnh sắc nét, trải nghiệm mượt mà và các tính năng hiện đại. Với tấm nền IPS 24 inch, màn hình này mang lại góc nhìn siêu rộng 178 độ, đảm bảo màu sắc và độ tương phản không thay đổi dù bạn nhìn từ bất kỳ góc độ nào. Điều này lý tưởng cho công việc đồ họa, chỉnh sửa ảnh hoặc chỉ đơn giản là thưởng thức nội dung đa phương tiện cùng gia đình.\n\nĐộ phân giải 2K QHD (2560 x 1440) cung cấp mật độ điểm ảnh cao hơn đáng kể so với Full HD, cho phép hiển thị hình ảnh chi tiết rõ ràng, văn bản sắc nét và không gian làm việc rộng rãi hơn, từ đó nâng cao hiệu suất làm việc. Tần số quét 75Hz vượt trội so với 60Hz truyền thống, mang lại trải nghiệm xem mượt mà hơn trong các cảnh chuyển động nhanh, đặc biệt hữu ích khi xem phim hành động, lướt web hoặc chơi game giải trí nhẹ.\n\nCông nghệ AMD FreeSync™ được tích hợp giúp đồng bộ hóa tần số quét của màn hình với card đồ họa tương thích, loại bỏ hoàn toàn hiện tượng xé hình và giật lag, đảm bảo trải nghiệm chơi game liền mạch và chân thực hơn bao giờ hết. Hơn nữa, với khả năng hỗ trợ HDR10, màn hình mở rộng dải tương phản động, giúp tái tạo màu đen sâu hơn, màu trắng sáng hơn và dải màu rộng hơn, mang đến hình ảnh sống động và chân thực, đặc biệt khi thưởng thức các nội dung HDR.\n\nThiết kế viền mỏng ba cạnh không chỉ mang lại không gian hiển thị rộng rãi hơn mà còn tạo nên vẻ ngoài hiện đại, tinh tế, dễ dàng hòa nhập vào mọi không gian làm việc hoặc giải trí. Với tất cả những tính năng nổi bật này, LG 24QP500-B là một màn hình đa năng, đáp ứng tốt từ nhu cầu làm việc chuyên nghiệp đến giải trí hàng ngày, mang lại giá trị vượt trội với mức giá hợp lý.',
        1, 'https://product.hstatic.net/200000722513/product/500-b-24-ips-2k-75hz-freesync-hdr10-1_fafefcddc82444fea1481e94f056ec3e_de6a37748e6d427abe6674b439223c02.jpg'
    );
