INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Samsung LS25BG400 25" IPS 240Hz HDR10 Gsync', 'man-hinh-samsung-ls25bg400-25-ips-240hz-hdr10-gsync', 16,
        '{"Kích thước": "558.5 x 341.1 x 85.4 mm (Không chân đế), 558.5 x 552.9 x 244.8 mm (Có chân đế)", "Tỷ lệ khung hình": "16:9", "Tấm nền": "IPS", "Góc nhìn": "178°/178°", "Độ phân giải": "Full HD 1920x1080", "Độ sáng": "400 cd/m²", "Tỷ lệ tương phản tĩnh": "1000:1", "Màu sắc hiển thị": "16.7 triệu màu", "Thời gian đáp ứng": "1ms", "Tần số quét": "240Hz", "Tính năng": "HDR10, Eco Saving Plus, Eco Light Sensor, Eye Saver Mode, Flicker Free, FreeSync Premium, Off Timer Plus, Black Equalizer, Low Input Lag Mode, Virtual AIM Point, Super Arena Gaming UX, Auto Source Switch+, Adaptive Picture, Ultrawide Game View", "Cổng kết nối": "2 x HDMI 2.0, 1 x DisplayPort", "Công suất tiêu thụ điện": "35 W", "Hỗ trợ": "Treo tường 100 x 100", "Trọng lượng": "2.8 kg (Không chân đế), 4.5 kg (Có chân đế)", "Phụ kiện": "Cáp nguồn, Cáp HDMI, Hướng dẫn sử dụng,..."}',
        'Màn hình Samsung LS25BG400 25\" IPS 240Hz HDR10 Gsync là lựa chọn hoàn hảo cho game thủ chuyên nghiệp và những ai tìm kiếm trải nghiệm hình ảnh đỉnh cao. Với kích thước 25 inch, tấm nền IPS cao cấp mang lại góc nhìn rộng và màu sắc trung thực, sống động đến từng chi tiết. Điểm nổi bật nhất của chiếc màn hình này chính là tần số quét cực nhanh 240Hz, đảm bảo mọi chuyển động trên màn hình đều mượt mà một cách kinh ngạc, loại bỏ hoàn toàn hiện tượng giật hình hay xé hình, giúp người dùng phản ứng nhanh hơn trong các tựa game đòi hỏi tốc độ cao. Công nghệ HDR10 tích hợp nâng cao đáng kể độ tương phản và dải màu, mang đến hình ảnh sâu sắc hơn với các vùng sáng chói và tối sâu thẳm, tái tạo cảnh quan game và nội dung đa phương tiện một cách chân thực nhất. Hơn nữa, với khả năng tương thích G-Sync (NVIDIA G-Sync Compatible) và FreeSync Premium, màn hình đồng bộ hóa tốc độ khung hình của card đồ họa với tần số quét của màn hình, loại bỏ hiện tượng rách hình và giật lag, đảm bảo trải nghiệm chơi game không gián đoạn. Thời gian phản hồi nhanh chóng (thường là 1ms GtG) giúp giảm thiểu hiện tượng bóng mờ, giữ cho hình ảnh luôn sắc nét ngay cả trong những pha hành động nhanh nhất. Thiết kế công thái học cho phép điều chỉnh độ cao, xoay, nghiêng và pivot, mang lại sự thoải mái tối đa trong mọi tư thế sử dụng. Với độ phân giải Full HD (1920x1080), màn hình cung cấp hình ảnh rõ ràng và chi tiết, tối ưu cho chơi game tốc độ cao mà không làm quá tải GPU. Các cổng kết nối đa dạng như HDMI và DisplayPort giúp dễ dàng kết nối với nhiều thiết bị khác nhau. Đây thực sự là một màn hình chuyên dụng, nâng tầm mọi trải nghiệm giải trí và làm việc của bạn lên một tầm cao mới, mang lại lợi thế cạnh tranh không nhỏ trong thế giới ảo.',
        1, 'https://product.hstatic.net/200000722513/product/-ls25bg400-25-ips-240hz-hdr10-gsync-1_ada778e547ab48de9271b15e7922085c_161d452dcdde4981b68d368640685a8f.jpg'
    );
