INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC Q27G42ZE 27" Fast IPS 2K 260Hz chuyên game', 'man-hinh-aoc-q27g42ze-27-fast-ips-2k-260hz-chuyen-game', 54,
        '{"Nhà sản xuất": "AOC"}',
        'Màn hình AOC Q27G42ZE 27\" Fast IPS 2K 260Hz là lựa chọn hoàn hảo cho game thủ chuyên nghiệp và những ai tìm kiếm trải nghiệm gaming đỉnh cao. Với tấm nền Fast IPS tiên tiến, màn hình này mang lại màu sắc sống động, độ tương phản ấn tượng và góc nhìn rộng, đảm bảo mọi chi tiết trong game đều hiển thị sắc nét và chân thực. Độ phân giải 2K QHD (2560x1440) trên kích thước 27 inch cung cấp không gian hiển thị rộng rãi, đồng thời giữ được mật độ điểm ảnh cao, cho hình ảnh cực kỳ rõ ràng, từ những cảnh hành động tốc độ cao đến các chi tiết nhỏ nhất trong môi trường game. Điểm nổi bật nhất của AOC Q27G42ZE chính là tần số quét siêu tốc 260Hz, kết hợp cùng thời gian phản hồi cực nhanh, giúp loại bỏ hoàn toàn hiện tượng xé hình, giật lag và bóng mờ. Người chơi sẽ cảm nhận được sự mượt mà tuyệt đối trong từng chuyển động, mang lại lợi thế cạnh tranh đáng kể trong các tựa game eSports. Công nghệ Adaptive Sync (tương thích FreeSync) đảm bảo đồng bộ hóa hoàn hảo giữa GPU và màn hình, cho trải nghiệm không gián đoạn. Thiết kế công thái học cho phép điều chỉnh độ cao, nghiêng và xoay, tối ưu hóa tư thế ngồi và giảm mỏi mắt khi chơi game trong thời gian dài. Viền màn hình siêu mỏng còn tạo điều kiện lý tưởng cho việc thiết lập đa màn hình, mở rộng không gian chiến đấu. AOC Q27G42ZE không chỉ là một màn hình, mà là một công cụ mạnh mẽ giúp bạn thống trị mọi trận chiến.',
        1, 'https://cdn.hstatic.net/products/200000722513/aoc_q27g42ze_gearvn_4db97f27183242bdb7067d0ccfb9aa19.jpg'
    );
