INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC U27G4 27" Fast IPS 4K 160Hz-FHD 320Hz chuyên game', 'man-hinh-aoc-u27g4-27-fast-ips-4k-160hz-fhd-320hz-chuyen-game', 54,
        '{"Kích thước": "27 inch","Tấm nền": "Fast IPS","Độ phân giải tối đa": "4K UHD (3840 x 2160) @ 160Hz","Chế độ Full HD": "1920 x 1080 @ 320Hz","Tốc độ phản hồi": "0.3ms MPRT (ở 320Hz), 0.5ms MPRT (ở 160Hz), 1ms GTG","Tần số quét": "Lên đến 320Hz","Độ sáng": "350 cd/m² (Typical), 400 nits (Peak)","Tỉ lệ tương phản": "1000:1 (Static), 80,000,000:1 (Dynamic)","Dải màu": "127% sRGB, 99% DCI-P3, 108% NTSC","Độ chính xác màu": "Delta E < 2","Công nghệ": "Flicker-Free, Low Blue Light, VESA DisplayHDR™ 400, Adaptive-Sync","Cổng kết nối": "2x HDMI 2.1, 1x DisplayPort 1.4, 3.5mm Audio Out","VESA mount": "100x100 mm","Tính năng đặc biệt": "Dual Refresh Rate (4K 160Hz / FHD 320Hz)"}',
        'Màn hình AOC U27G4 27\" Fast IPS 4K 160Hz - FHD 320Hz chuyên game là một tuyệt phẩm công nghệ được thiết kế dành riêng cho game thủ chuyên nghiệp và những người đam mê trải nghiệm hình ảnh đỉnh cao. Với kích thước 27 inch lý tưởng, màn hình này mang đến không gian hiển thị rộng rãi, kết hợp cùng tấm nền Fast IPS tiên tiến, đảm bảo tái tạo màu sắc sống động, độ tương phản ấn tượng và góc nhìn rộng, chân thực từ mọi góc độ.\n\nĐiểm nhấn vượt trội của U27G4 nằm ở khả năng hiển thị độ phân giải 4K sắc nét tuyệt đối ở tần số quét 160Hz, cho phép người chơi đắm chìm vào thế giới game với từng chi tiết được tái hiện sống động, mượt mà đến khó tin, làm tăng cường đáng kể trải nghiệm thị giác trong các tựa game đồ họa nặng.\n\nKhông chỉ dừng lại ở đó, AOC U27G4 còn đột phá với chế độ FHD (Full HD) đạt tần số quét lên đến 320Hz, một con số đáng kinh ngạc, đáp ứng hoàn hảo nhu cầu của các game thủ esports cạnh tranh, nơi mỗi khung hình, mỗi mili giây đều quyết định thắng bại. Sự kết hợp giữa tốc độ phản hồi cực nhanh của tấm nền Fast IPS và tần số quét cao ngất ngưởng giúp loại bỏ hoàn toàn hiện tượng xé hình, giật lag hay bóng mờ chuyển động, mang lại lợi thế tối đa trong mọi cuộc đối đầu nảy lửa.\n\nSản phẩm còn được trang bị đầy đủ các cổng kết nối hiện đại (như DisplayPort, HDMI) và chân đế công thái học đa năng, cho phép điều chỉnh độ cao, xoay, nghiêng linh hoạt, tối ưu hóa sự thoải mái khi sử dụng trong thời gian dài, giúp game thủ duy trì sự tập trung và phong độ.\n\nAOC U27G4 chính là lựa chọn hoàn hảo cho những ai tìm kiếm sự kết hợp đỉnh cao giữa độ phân giải siêu nét, tốc độ siêu nhanh và trải nghiệm chơi game mượt mà không đối thủ, định nghĩa lại chuẩn mực của một màn hình chơi game cao cấp.',
        1, 'https://cdn.hstatic.net/products/200000722513/aoc_u27g4_gearvn_4b5b9061eb8542388e972adfce313d5c.jpg'
    );
