INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ViewSonic ColorPro VP2788-5K 27" IPS 5K USBC chuyên đồ hoạ', 'man-hinh-viewsonic-colorpro-vp2788-5k-27-ips-5k-usbc-chuyen-do-hoa', 55,
        '{"Nhà sản xuất": "ViewSonic"}',
        'Màn hình ViewSonic ColorPro VP2788-5K là sự lựa chọn hoàn hảo dành cho các chuyên gia đồ họa, nhiếp ảnh gia và những người sáng tạo nội dung yêu cầu độ chính xác màu sắc tuyệt đối và chi tiết hình ảnh đỉnh cao. Với kích thước 27 inch và độ phân giải 5K (5120 x 2880), màn hình này mang đến mật độ điểm ảnh siêu cao, cho phép hiển thị hình ảnh sắc nét đến từng chi tiết, loại bỏ hiện tượng răng cưa và pixel hóa, vô cùng lý tưởng cho việc chỉnh sửa ảnh độ phân giải cao, thiết kế đồ họa phức tạp và dựng phim 4K/5K. Tấm nền IPS cao cấp đảm bảo góc nhìn rộng 178 độ, giữ cho màu sắc và độ sáng đồng nhất từ mọi góc độ, điều cực kỳ quan trọng trong môi trường làm việc nhóm hoặc khi xem lại tác phẩm. Dòng ColorPro của ViewSonic nổi tiếng với khả năng tái tạo màu sắc vượt trội, thường đạt Delta E < 2, cùng với độ phủ màu rộng rãi các không gian màu chuẩn công nghiệp như 100% sRGB, Adobe RGB và DCI-P3, đảm bảo rằng màu sắc bạn thấy trên màn hình sẽ chính xác như thành phẩm cuối cùng. Cổng kết nối USB-C đa năng là một điểm cộng lớn, cho phép truyền tải video, dữ liệu và sạc nguồn (Power Delivery) chỉ bằng một cáp duy nhất, giúp bàn làm việc của bạn gọn gàng hơn và tối ưu hóa quy trình làm việc. Chân đế công thái học cho phép điều chỉnh độ cao, nghiêng, xoay và xoay dọc (pivot), giúp người dùng dễ dàng tìm được tư thế làm việc thoải mái nhất, giảm thiểu mỏi mắt và cổ trong suốt thời gian dài sử dụng. ViewSonic ColorPro VP2788-5K thực sự là một công cụ mạnh mẽ, nâng tầm trải nghiệm sáng tạo của bạn lên một đẳng cấp mới.',
        1, 'https://product.hstatic.net/200000722513/product/view_vp2788-5k_gearvn_76da39f78cf642bca2714f9ada263142.jpg'
    );
