INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ASUS ProArt PA27JCV 27" IPS 5K USBC chuyên đồ họa', 'man-hinh-asus-proart-pa27jcv-27-ips-5k-usbc-chuyen-do-hoa', 3,
        '{"Nhà sản xuất": "Asus"}',
        'Màn hình ASUS ProArt PA27JCV 27\" IPS 5K USB-C chuyên đồ họa là một công cụ mạnh mẽ và không thể thiếu cho các nhà sáng tạo nội dung chuyên nghiệp, từ nhiếp ảnh gia, biên tập viên video đến các nhà thiết kế đồ họa. Với tấm nền IPS kích thước 27 inch, màn hình này mang lại góc nhìn rộng và màu sắc nhất quán, đảm bảo hình ảnh hiển thị trung thực từ mọi góc độ. Điểm nhấn ấn tượng nhất chính là độ phân giải 5K (5120 x 2880), cung cấp mật độ điểm ảnh siêu cao, cho phép hiển thị từng chi tiết nhỏ nhất của tác phẩm với độ sắc nét vượt trội, giúp người dùng dễ dàng chỉnh sửa và tinh chỉnh. Khả năng tái tạo màu sắc của PA27JCV được hiệu chỉnh tại nhà máy với độ chính xác Delta E < 2, đảm bảo màu sắc chân thực ngay từ khi xuất xưởng. Màn hình hỗ trợ dải màu rộng, bao gồm 99% DCI-P3, 99% Adobe RGB và 100% sRGB, đáp ứng mọi tiêu chuẩn khắt khe nhất trong ngành đồ họa và in ấn. Công nghệ ProArt Palette độc quyền của ASUS cho phép tùy chỉnh các thông số màu sắc một cách dễ dàng, mang lại sự linh hoạt tối đa. Cổng kết nối USB-C đa năng là một điểm cộng lớn, hỗ trợ truyền tải video, dữ liệu và cung cấp nguồn điện lên đến 96W chỉ với một sợi cáp duy nhất, giúp không gian làm việc gọn gàng và tối ưu hóa quy trình làm việc. Ngoài ra, màn hình còn được trang bị các cổng HDMI, DisplayPort và một hub USB, tăng cường khả năng kết nối với nhiều thiết bị ngoại vi. Chân đế công thái học cho phép điều chỉnh độ cao, xoay ngang, xoay dọc và nghiêng, mang lại tư thế làm việc thoải mái nhất trong thời gian dài. ASUS ProArt PA27JCV thực sự là một khoản đầu tư xứng đáng cho bất kỳ ai đặt nặng về chất lượng hình ảnh và độ chính xác màu sắc.',
        1, 'https://product.hstatic.net/200000722513/product/asus_pa27jcv_gearvn_9484e4bd73df435ba454ba5f504fcbc7.jpg'
    );
