INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Asus TUF GAMING VG279QE5A-R 27" IPS 146Hz chuyên game', 'man-hinh-asus-tuf-gaming-vg279qe5a-r-27-ips-146hz-chuyen-game', 3,
        '{"Nhà sản xuất": "ASUS"}',
        'Màn hình Asus TUF GAMING VG279QE5A-R 27 inch IPS 146Hz là lựa chọn hoàn hảo cho game thủ tìm kiếm trải nghiệm đỉnh cao. Với tấm nền IPS kích thước 27 inch, màn hình này mang lại màu sắc sống động, độ tương phản tuyệt vời và góc nhìn siêu rộng 178 độ, đảm bảo mọi chi tiết trong game đều hiển thị rõ nét và chân thực. Tốc độ làm mới 146Hz cực nhanh loại bỏ hiện tượng xé hình và giật lag, cho phép bạn phản ứng nhanh chóng trong các pha hành động kịch tính. Thời gian phản hồi MPRT 1ms (Moving Picture Response Time) giúp loại bỏ bóng mờ và nhòe chuyển động, đảm bảo hình ảnh mượt mà ngay cả trong các tựa game tốc độ cao. Công nghệ ASUS Extreme Low Motion Blur (ELMB) Sync tích hợp cùng Adaptive Sync (FreeSync Premium) mang lại trải nghiệm chơi game không gián đoạn, mượt mà đến khó tin. Màn hình còn được trang bị các tính năng độc quyền của ASUS như GamePlus (hiển thị tâm ngắm, hẹn giờ, bộ đếm FPS, căn chỉnh màn hình) và GameVisual với nhiều chế độ hiển thị tối ưu cho từng thể loại game. Công nghệ Shadow Boost giúp làm sáng các khu vực tối mà không làm phơi sáng quá mức các vùng sáng, cho phép bạn dễ dàng phát hiện kẻ địch ẩn nấp. Ngoài ra, công nghệ Flicker-Free và Low Blue Light bảo vệ mắt bạn khỏi mỏi khi chơi game trong thời gian dài. Thiết kế công thái học cho phép điều chỉnh độ nghiêng, xoay và chiều cao, mang lại sự thoải mái tối đa. Với cổng kết nối đa dạng như DisplayPort và HDMI, VG279QE5A-R dễ dàng kết nối với nhiều thiết bị. Đây không chỉ là một màn hình, mà là một công cụ chiến thắng giúp bạn thống trị mọi trận chiến ảo.',
        1, 'https://product.hstatic.net/200000722513/product/asus_vg279qe5a-r_gearvn_6188c0e4ab7f4752921a84e66398de3a.jpg'
    );
