INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình AOC Q27G11E 27" IPS 2K 180Hz chuyên game', 'man-hinh-aoc-q27g11e-27-ips-2k-180hz-chuyen-game', 54,
        '{"Nhà sản xuất": "AOC"}',
        'Màn hình AOC Q27G11E 27\" IPS 2K 180Hz chuyên game là một cỗ máy hiển thị mạnh mẽ, được thiết kế đặc biệt để đáp ứng nhu cầu khắt khe của các game thủ chuyên nghiệp và đam mê. Với kích thước 27 inch rộng rãi và tấm nền IPS chất lượng cao, màn hình này mang lại hình ảnh sắc nét, màu sắc sống động và độ chính xác vượt trội, cùng với góc nhìn cực rộng, giúp mọi chi tiết trong thế giới game được tái hiện chân thực và rực rỡ nhất. Độ phân giải 2K (QHD 2560x1440) cung cấp mật độ điểm ảnh cao hơn nhiều so với Full HD, cho phép bạn đắm chìm hoàn toàn vào các cảnh quan đồ họa phức tạp với chi tiết đáng kinh ngạc. Điểm nhấn mạnh mẽ nhất của AOC Q27G11E chính là tần số quét 180Hz siêu nhanh, một bước tiến đáng kể so với chuẩn 144Hz, đảm bảo mọi chuyển động trên màn hình đều mượt mà tuyệt đối, không còn hiện tượng xé hình hay giật lag. Kết hợp với thời gian phản hồi cực thấp 1ms MPRT (Moving Picture Response Time), màn hình loại bỏ hoàn toàn hiện tượng bóng ma, mang lại lợi thế cạnh tranh quyết định trong các tựa game yêu cầu tốc độ phản xạ cao như FPS hay MOBA. Công nghệ Adaptive Sync (thường là FreeSync Premium) được tích hợp sẵn giúp đồng bộ hóa tần số quét của màn hình với tốc độ khung hình của card đồ họa, ngăn chặn hiệu quả tình trạng xé hình và giật hình, đảm bảo trải nghiệm chơi game liền mạch và không bị gián đoạn. Thiết kế công thái học tiên tiến với chân đế có khả năng điều chỉnh độ cao, xoay, nghiêng và xoay dọc (pivot) cho phép bạn tùy chỉnh vị trí màn hình một cách linh hoạt, giảm thiểu mỏi mắt và cổ khi chơi game trong thời gian dài. Viền màn hình siêu mỏng (frameless design) không chỉ tăng cường tính thẩm mỹ mà còn tối ưu hóa không gian hiển thị, lý tưởng cho việc thiết lập đa màn hình. AOC Q27G11E không chỉ là một chiếc màn hình, mà là một công cụ mạnh mẽ giúp bạn nâng tầm kỹ năng và thống trị mọi chiến trường ảo.',
        1, 'https://product.hstatic.net/200000722513/product/aoc_q27g11e_gearvn_94906e7b704c43b9bdf3511170a0c375.jpg'
    );
