INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình GIGABYTE M27QA 27" IPS 2K 180Hz chuyên game', 'man-hinh-gigabyte-m27qa-27-ips-2k-180hz-chuyen-game', 49,
        '{"Nhà sản xuất": "GIGABYTE"}',
        'Màn hình GIGABYTE M27QA 27\" IPS 2K 180Hz chuyên game là lựa chọn hoàn hảo cho mọi game thủ và những người làm công việc đòi hỏi độ chính xác màu sắc cao. Với kích thước 27 inch cùng độ phân giải QHD (2560x1440), màn hình mang đến hình ảnh cực kỳ sắc nét, chi tiết sống động, giúp bạn đắm chìm hoàn toàn vào thế giới ảo hoặc các tác phẩm đồ họa. Tấm nền IPS cao cấp không chỉ đảm bảo màu sắc rực rỡ, chân thực với độ phủ màu rộng, mà còn mang lại góc nhìn siêu rộng 178 độ, giữ nguyên chất lượng hình ảnh dù nhìn từ bất kỳ vị trí nào.\n\nĐiểm nhấn mạnh mẽ nhất của M27QA chính là tần số quét 180Hz siêu nhanh và thời gian phản hồi 1ms MPRT, loại bỏ hoàn toàn hiện tượng xé hình, giật lag hay bóng mờ, mang lại chuyển động mượt mà, phản ứng tức thì trong mọi trận chiến căng thẳng. Công nghệ AMD FreeSync Premium (hoặc tương thích G-Sync) đồng bộ hóa tần số quét của màn hình với card đồ họa, đảm bảo trải nghiệm chơi game không gián đoạn, không lỗi hình. Màn hình còn hỗ trợ công nghệ HDR, tăng cường độ tương phản và chi tiết ở cả vùng sáng và tối, giúp hình ảnh có chiều sâu và sống động hơn.\n\nThiết kế công thái học cho phép điều chỉnh linh hoạt độ cao, xoay ngang, nghiêng và xoay dọc (pivot), giúp người dùng dễ dàng tìm được tư thế thoải mái nhất, giảm mỏi mắt và cổ khi sử dụng trong thời gian dài. Ngoài ra, tính năng KVM tích hợp độc đáo của Gigabyte cho phép bạn điều khiển nhiều thiết bị (PC, laptop) chỉ với một bộ bàn phím, chuột và màn hình duy nhất, tối ưu hóa không gian làm việc.\n\nVới GIGABYTE M27QA, mọi pha hành động đều trở nên sống động, mượt mà và chính xác hơn bao giờ hết, đưa bạn đến với đỉnh cao của trải nghiệm gaming và hiệu suất công việc.',
        1, 'https://product.hstatic.net/200000722513/product/giga_m27qa_gearvn_94bf2b8fca1f40e7a14fcdc93e7eb2df.jpg'
    );
