INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình LG 27GS65F-B 27" IPS 180Hz HDR10 Gsync chuyên game', 'man-hinh-lg-27gs65f-b-27-ips-180hz-hdr10-gsync-chuyen-game', 12,
        '{"Nhà sản xuất": "LG"}',
        'Màn hình LG 27GS65F-B là sự lựa chọn hoàn hảo dành cho các game thủ chuyên nghiệp và những người đam mê trải nghiệm hình ảnh đỉnh cao. Với kích thước 27 inch rộng rãi, tấm nền IPS cao cấp mang lại góc nhìn siêu rộng 178 độ cùng khả năng tái tạo màu sắc sống động, chân thực đến từng chi tiết. Độ phân giải Full HD sắc nét đảm bảo hình ảnh rõ ràng mà vẫn tối ưu hiệu năng cho hệ thống gaming của bạn.\n\nĐiểm nhấn ấn tượng nhất của chiếc màn hình này chính là tần số quét cực nhanh 180Hz, loại bỏ hoàn toàn hiện tượng xé hình và giật lag, mang đến sự mượt mà tuyệt đối trong mọi khung hình, từ những pha hành động tốc độ cao cho đến các cảnh quay phức tạp. Không chỉ dừng lại ở đó, thời gian phản hồi siêu tốc 1ms (GtG) là yếu tố then chốt giúp game thủ phản ứng nhanh nhạy, giành lợi thế trong từng trận đấu.\n\nCông nghệ HDR10 được tích hợp cho phép màn hình hiển thị dải tương phản động rộng hơn, màu đen sâu hơn và màu trắng sáng hơn, tạo nên những hình ảnh có chiều sâu và độ sống động vượt trội. Đặc biệt, màn hình tương thích với NVIDIA G-SYNC, đồng bộ hóa tần số quét của màn hình với GPU của card đồ họa, loại bỏ hiện tượng xé hình và giật hình khó chịu, đảm bảo trải nghiệm chơi game liền mạch và không gián đoạn.\n\nThiết kế công thái học với khả năng điều chỉnh độ cao, nghiêng, xoay và xoay dọc (pivot) giúp người dùng dễ dàng tìm được tư thế ngồi thoải mái nhất, hỗ trợ chơi game hoặc làm việc trong thời gian dài mà không mỏi. Các cổng kết nối đa dạng như HDMI và DisplayPort cũng đảm bảo khả năng tương thích rộng rãi với nhiều thiết bị.\n\nLG 27GS65F-B thực sự là một vũ khí sắc bén giúp game thủ nâng tầm kỹ năng và đắm chìm hoàn toàn vào thế giới ảo.',
        1, 'https://product.hstatic.net/200000722513/product/lg_27gs65f-b_gearvn_2650af629116476588bb61972092b90f.jpg'
    );
