INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Acer KG270-X1 27" IPS 200Hz Gsync chuyên game', 'man-hinh-acer-kg270-x1-27-ips-200hz-gsync-chuyen-game', 35,
        '{"Tên sản phẩm":"Màn hình Acer KG270-X1 27\" IPS 200Hz G-Sync","Kích thước màn hình":"27 inch","Kiểu màn hình":"Phẳng","Tấm nền":"IPS","Tỷ lệ":"16:9","Độ phân giải":"1920 x 1080 (Full HD)","Tần số quét":"200Hz","Thời gian phản hồi":"1ms","Số lượng màu":"16.7 triệu màu","Độ phủ màu":"72% NTSC, 99% sRGB","Độ sáng":"250 nits","Độ tương phản tĩnh":"1000:1","Độ tương phản động":"100,000,000:1","Công nghệ":"HDR10, Acer BluelightShield, Acer VisionCare, Acer Low-Dimming, Acer ComfyView","Điều chỉnh":"Nghiêng -5°~20°","Cổng kết nối":"2x HDMI 2.0, 1x DisplayPort 1.4","Tính năng":"Hạn chế ánh sáng xanh, Chống nháy, AMD FreeSync Premium, G-Sync Compatible"}',
        'Màn hình Acer KG270-X1 27\" IPS 200Hz Gsync chuyên game là một cỗ máy hoàn hảo dành cho các game thủ đam mê tốc độ và trải nghiệm hình ảnh đỉnh cao. Sở hữu tấm nền IPS kích thước 27 inch, màn hình mang đến góc nhìn rộng rãi, màu sắc chân thực và độ chính xác màu sắc tuyệt vời, tái tạo mọi chi tiết trong thế giới game sống động. Tần số quét 200Hz siêu tốc giúp loại bỏ hoàn toàn hiện tượng giật hình, xé màn, mang đến những pha hành động mượt mà đến không ngờ. Công nghệ NVIDIA G-Sync đồng bộ hóa tần số quét của màn hình với tốc độ khung hình của GPU, loại bỏ hiện tượng xé hình và giảm thiểu độ trễ, cho bạn lợi thế quyết định trong mọi trận đấu. Độ phân giải Full HD (1920x1080) cùng với thời gian phản hồi 1ms (VRB) đảm bảo mọi khung hình đều sắc nét, không mờ nhòe, ngay cả trong những tình huống giao tranh tốc độ cao. \n\nAcer KG270-X1 còn được trang bị các công nghệ bảo vệ mắt tiên tiến như BluelightShield và Flickerless, giúp giảm thiểu mỏi mắt khi sử dụng trong thời gian dài. Thiết kế hiện đại với viền màn hình mỏng, chân đế vững chắc không chỉ tăng tính thẩm mỹ cho không gian giải trí mà còn mang lại sự thoải mái khi tùy chỉnh góc nhìn. Đây là lựa chọn lý tưởng cho những ai tìm kiếm hiệu năng chơi game vượt trội, hình ảnh chất lượng cao và trải nghiệm mượt mà nhất.\n\n**Thiết kế hiện đại và mạnh mẽ**\n\nMàn hình có thiết kế viền mỏng ở 3 cạnh, giúp tối ưu hóa không gian hiển thị và mang đến vẻ ngoài hiện đại. Chân đế được thiết kế chắc chắn, cho phép điều chỉnh độ nghiêng màn hình để bạn có được góc nhìn thoải mái nhất. Mặt sau của màn hình cũng được chăm chút tỉ mỉ, với các đường vân và họa tiết gaming, tạo nên một tổng thể hài hòa và cuốn hút.\n\n**Các cổng kết nối đa dạng**\n\nAcer KG270-X1 được trang bị đầy đủ các cổng kết nối phổ biến như HDMI và DisplayPort, đảm bảo khả năng tương thích tốt với nhiều loại thiết bị khác nhau, từ PC gaming, console, đến laptop.\n\n**Công nghệ bảo vệ mắt** \nAcer cũng trang bị cho KG270-X1 các công nghệ bảo vệ mắt như Flicker-less (khử nhấp nháy) và Blue Light Filter (lọc ánh sáng xanh), giúp giảm thiểu tình trạng mỏi mắt khi bạn chơi game trong thời gian dài. Điều này đặc biệt quan trọng đối với các game thủ chuyên nghiệp, những người thường xuyên phải ngồi trước màn hình nhiều giờ liên tục.',
        1, 'https://product.hstatic.net/200000722513/product/acer_kg270_x1_gearvn_15f0f9177bba487197fa984aac42d623.jpg'
    );
