INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình Asus ROG Strix XG27UCG 27" Fast IPS 4K 160Hz-FHD 320Hz chuyên game', 'man-hinh-asus-rog-strix-xg27ucg-27-fast-ips-4k-160hz-fhd-320hz-chuyen-game', 3,
        '{"Tên sản phẩm":"Asus ROG Strix XG27UCG 27\" Fast IPS 4K 160Hz-FHD 320Hz","Kích thước màn hình":"27 inch","Kiểu màn hình":"Phẳng","Tấm nền":"Fast IPS","Độ phân giải":"3840 x 2160 (4K)","Tần số quét":"160Hz (4K) / 320Hz (FHD)","Thời gian phản hồi":"1ms","Độ sáng":"350 cd/m² (Typ.)","Không gian màu":"95% DCI-P3, 130% sRGB, 10-bit","Tương thích VESA":"100 x 100 mm","Cổng kết nối":"1x HDMI 2.1, 1x DisplayPort 1.4, 1x USB-C (DP Alt Mode)","Tính năng":"Chống nháy, chế độ kép 4K 160Hz hoặc FHD 320Hz","Phụ kiện trong hộp":"Dây nguồn, dây HDMI (tùy chọn), dây DisplayPort (tùy chọn)"}',
        'Màn hình Asus ROG Strix XG27UCG 27\" Fast IPS 4K 160Hz-FHD 320Hz\n\nAsus ROG Strix XG27UCG không chỉ là một chiếc màn hình máy tính, đó là cả một cuộc cách mạng về tốc độ và hình ảnh. Với tần số quét 160Hz ở độ phân giải 4K và 320Hz ở Full HD, cùng tấm nền Fast IPS tiên tiến, XG27UCG sẽ mang đến cho bạn những trận game mượt mà, sống động và đầy kịch tính. \n\nĐỉnh cao của độ phân giải và tốc độ\nROG Strix XG27UCG sở hữu màn hình 27 inchFast IPS với độ phân giải 4K UHD (3840 x 2160), mang đến hình ảnh sắc nét đến từng chi tiết, màu sắc sống động và độ tương phản tuyệt vời. Nhờ đó, bạn sẽ được tận hưởng những khung cảnh game tuyệt đẹp, sống động như thật.\n\nKhông chỉ dừng lại ở độ phân giải, XG27UCG còn sở hữu tần số quét ấn tượng 160Hz ở độ phân giải 4K và 320Hz ở độ phân giải Full HD. Tần số quét siêu cao này giúp giảm thiểu hiện tượng giật lag, xé hình, mang đến trải nghiệm chơi game mượt mà, không gián đoạn. Thời gian phản hồi chỉ 1ms (GTG) giúp loại bỏ hiện tượng bóng ma, cho phép bạn phản ứng nhanh chóng với mọi tình huống trong game.\n\nMàn hình Asus ROG Strix XG27UCG 27\n\nCông nghệ tiên tiến cho trải nghiệm gaming tuyệt đỉnh\nROG Strix XG27UCG được trang bị hàng loạt công nghệ tiên tiến, được thiết kế đặc biệt để tối ưu hóa trải nghiệm gaming.\n\nCông nghệ Fast IPS: Màn hình Fast IPS giúp cải thiện thời gian phản hồi, đồng thời duy trì góc nhìn rộng và màu sắc chính xác.\n\nCông nghệ ELMB Sync: Công nghệ Extreme Low Motion Blur Sync (ELMB Sync) kết hợp ELMB và Adaptive-sync, giúp giảm thiểu hiện tượng nhòe chuyển động, mang đến hình ảnh sắc nét ngay cả trong những cảnh hành động nhanh.\n\nCông nghệ DisplayHDR™ 400: Với chứng nhận DisplayHDR™ 400, XG27UCG có khả năng hiển thị độ tương phản cao, màu đen sâu hơn và màu trắng sáng hơn, giúp hình ảnh sống động và chân thực hơn.\n\nCông nghệ Adaptive-Sync: Hỗ trợ công nghệ Adaptive-Sync (tương thích với cả NVIDIA G-SYNC và AMD FreeSync), XG27UCG giúp loại bỏ hiện tượng xé hình, mang đến trải nghiệm chơi game mượt mà trên mọi hệ thống.\n\nMàn hình Asus ROG Strix XG27UCG 27\n\nThiết kế công thái học, đậm chất gaming\nROG Strix XG27UCG không chỉ mạnh mẽ về hiệu năng mà còn sở hữu thiết kế đậm chất gaming, với những đường nét góc cạnh, mạnh mẽ. Chân đế của màn hình Asus này có thể điều chỉnh linh hoạt độ cao, góc nghiêng và góc xoay, giúp bạn dễ dàng tìm được vị trí xem thoải mái nhất.\n\nKết nối đa dạng, tiện lợi\nROG Strix XG27UCG được trang bị đầy đủ các cổng kết nối, bao gồm DisplayPort 1.4, HDMI 2.1 và USB-C. Cổng USB-C hỗ trợ DisplayPort Alternate Mode (DP Alt Mode) và Power Delivery, cho phép bạn kết nối màn hình với laptop chỉ bằng một cáp duy nhất, đồng thời sạc pin cho laptop.',
        1, 'https://product.hstatic.net/200000722513/product/asus_xg27ucg_gearvn_dc91678e3a604d00907d30432cca83ff.jpg'
    );
