INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình cong LG 34GX90SA-W 34" OLED 2K 240Hz chuyên game', 'man-hinh-cong-lg-34gx90sa-w-oled-2k-240hz-chuyen-game', 12,
        '{"Kích thước màn hình":"34 inch","Kiểu màn hình":"Cong","Độ cong":"800R","Tấm nền":"OLED","Tỉ lệ màn hình":"21:9","Độ phân giải":"3440 x 1440","Tần số quét":"240Hz","Thời gian phản hồi":"0.03ms","Số lượng màu":"1.07 tỷ màu, 10-bit","Độ phủ màu":"DCI-P3 98.5% (CIE1976)","Độ sáng":"275 cd/m²","Độ tương phản tĩnh":"1500000:1","Công nghệ màn hình":"HDR10, Điều chỉnh độ sáng tự động, VRR, Crosshair","Nghiêng":"Có","Xoay":"Có","Điều chỉnh độ cao":"Có","Cổng kết nối":"HDMI, DisplayPort, USB-C, Đầu ra tai nghe, LAN (RJ-45)","Hệ thống âm thanh":"7W x 2","Tính năng":"Chống chói, Chế độ chơi game, Chống nháy, Tương thích NVIDIA G-Sync và AMD FreeSync Premium, VESA DisplayHDR 400","Tiện ích":"Chế độ đọc sách, Bluetooth, Hệ điều hành webOS, AI Picture, Dynamic Tone Mapping, AI Personalized Picture Wizard, AI Sound, Hexagon Lighting","Công suất tiêu thụ":"<0.5W (Tắt DC)","Kích thước":"Có chân đế: 784.1 x 580.4 x 291.8 mm (UP) / 784.1 x 470.4 x 291.8 mm (Down); Không chân đế: 784.1 x 358.3 x 167.1 mm","Trọng lượng":"Có chân đế: 10.4 kg, Không chân đế: 6.2 kg"}',
        'Màn hình cong LG 34GX90SA-W quảng bá một trải nghiệm chơi game tuyệt vời với các tính năng nổi bật và thiết kế hiện đại, hứa hẹn sẽ làm hài lòng cả những game thủ khó tính nhất. Dưới đây là bài đánh giá chi tiết về sản phẩm này.\n\nThiết kế hiện đại và ấn tượng\nVới độ cong 800R, LG 34GX90SA-W ôm trọn tầm nhìn của game thủ, kiến tạo một không gian hiển thị liền mạch và chân thực đến khó tin. Thiết kế này không chỉ tăng cường cảm giác nhập vai sâu sắc vào mọi trận chiến, mà còn tối ưu hóa sự thoải mái cho đôi mắt, giảm thiểu tình trạng mỏi mắt khi trải nghiệm game trong thời gian dài.\n\nMàn hình cong LG 34GX90SA-W 34\n\nĐộ phân giải và chất lượng hình ảnh vượt trội\nVới độ phân giải 3440 x 1440 và công nghệ OLED, màn hình này mang đến hình ảnh sắc nét và rực rỡ. Màu sắc được tái tạo chính xác với độ tương phản cao, giúp các chi tiết trong game trở nên sống động và chân thực hơn.\n\nTần số quét 240Hz và thời gian phản hồi 0.03ms\nĐây là những con số ấn tượng giúp loại bỏ hoàn toàn hiện tượng xé hình, giật lag hay bóng mờ, đảm bảo mọi chuyển động trên màn hình diễn ra mượt mà, nhanh nhạy đến từng khung hình. Game thủ sẽ phản ứng tức thì và chính xác, làm chủ mọi tình huống trong game.\n\nMàn hình cong LG 34GX90SA-W 34\n\nCông nghệ HDR cho trải nghiệm hình ảnh tốt nhất\nMàn hình LG 34GX90SA-W hỗ trợ công nghệ HDR10, mang đến dải màu rộng và độ sáng tối ưu. Nhờ vào khả năng tái tạo màu sắc chính xác, người chơi sẽ cảm nhận được sự khác biệt rõ rệt khi tham gia vào các trò chơi có đồ họa cao.\n\nCác tính năng hỗ trợ chơi game tối ưu\nMàn hình này còn được trang bị nhiều công nghệ hiện đại hỗ trợ chơi game như FreeSync và G-Sync, giúp đồng bộ hóa tần số quét giữa màn hình và card đồ họa, mang đến trải nghiệm chơi game mượt mà hơn, không bị xé hình.\n\nMàn hình cong LG 34GX90SA-W 34\n\nCông nghệ OLED với VESA DisplayHDR True Black 400\nMàn hình OLED mang đến khả năng hiển thị màu đen tuyệt đối, độ tương phản 1,5M:1 và dải màu DCI-P3 98.5% (điển hình), tái tạo hình ảnh với độ sâu và chi tiết đáng kinh ngạc. Từ những pha hành động bùng nổ đến những cảnh quay tối tăm đầy kịch tính, mọi sắc thái đều được thể hiện một cách chân thực và sống động nhất.\n\nKết nối đa dạng\nMàn hình LG 34GX90SA-W được trang bị nhiều cổng kết nối như HDMI 2.0, DisplayPort 1.4 và USB-C, giúp người dùng dễ dàng kết nối với nhiều thiết bị khác nhau, từ máy tính đến console một cách thuận tiện.\n\nMàn hình cong LG 34GX90SA-W 34\n\nMàn hình cong LG 34GX90SA-W 34\n\nBảo vệ đôi mắt \n\nLG thấu hiểu tầm quan trọng của việc bảo vệ đôi mắt cho game thủ. Công nghệ LG WOLED được UL xác minh giúp hạn chế tiếp xúc với ánh sáng xanh có hại và loại bỏ hiện tượng nhấp nháy (Flicker-Free, Discomfort Glare Free, Low Blue Light Hardware Solution Platinum). Bề mặt màn hình chống chói cũng là một điểm cộng lớn, giúp giảm phản xạ ánh sáng khó chịu, cho phép bạn tập trung hoàn toàn vào trận đấu.\n\nMàn hình cong LG 34GX90SA-W 34\n\nTính năng thông minh webOS\nKhông chỉ dừng lại ở hiệu năng gaming, LG 34GX90SA-W còn tích hợp hệ điều hành webOS thông minh. Điều này biến chiếc màn hình của bạn thành một trung tâm giải trí đa năng, nơi bạn có thể dễ dàng truy cập các ứng dụng, xem phim, nghe nhạc mà không cần đến thiết bị ngoại vi phức tạp.',
        1, 'https://product.hstatic.net/200000722513/product/lg_34gx90sa-w_gearvn_17a0185667854c7f984875a5e68ebd09.jpg'
    );
