INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình cong LG 34WR55QK-B 34" 2K 100Hz USBC', 'man-hinh-cong-lg-34wr55qk-b-34-2k-100hz-usbc', 12,
        '{"Kích thước màn hình": "34 inches", "Kiểu màn hình": "Màn hình cong", "Độ cong": "1800R", "Tấm nền": "VA", "Tỉ lệ màn hình": "21:9", "Tần số quét": "100 Hz", "Thời gian phản hồi": "5 ms", "Số lượng màu": "16.7 triệu màu", "Độ phủ màu": "sRGB 90% (CIE1931)", "Độ sáng": "240 cd/m²", "Góc nhìn": "178º/178º", "Độ tương phản tĩnh": "3000:1", "Công nghệ màn hình": "HDR10, Đồng bộ hành động động, Super Resolution+, Hiệu ứng HDR", "Nghiêng": "-5~20˚", "Điều chỉnh độ cao": "110 mm", "Treo tường": "100 x 100 mm", "Độ phân giải": "3440 x 1440 pixels", "Cổng kết nối": "2x HDMI, 1x DisplayPort, 1x USB-C, 1x Audio Out, 2x USB 3.0 Downstream", "Tính năng màn hình": "Chống nháy, Chế độ tiết kiệm điện", "Tiện ích": "Chế độ đọc sách, Ảnh kề ảnh (PBP)", "Công suất tiêu thụ": "Tắt DC <0.3W", "Kích thước": "Có chân đế: 809 x 568.3 x 260 mm (Up) / 809 x 458.3 x 260 mm (Down), Không chân đế: 809 x 358.9 x 91.5 mm", "Trọng lượng": "Có chân đế: 6.9 kg, Không chân đế: 5 kg"}',
        'Màn hình cong LG 34WR55QK-B 34\" 2K 100Hz USBC là một thành phần không thể thiếu trong mọi góc setup, màn hình luôn là một trong những lựa chọn đau đầu dành cho mọi người dùng, đặc biệt là với dân văn phòng. \n\nMột bản thiết kế hoàn hảo cho công việc\nVì sao LG 34WR55QK-B được gọi như vậy? Điều này đến từ sự chỉn chu trong từng góc cạnh và chi tiết mà LG mang lại cho sản phẩm của mình. Chiếc màn hình khoác lên mình màu đen lịch lãm cùng các chi tiết đơn giản, vô cùng phù hợp với môi trường văn phòng làm việc sôi động của từng công ty. Bên cạnh đó là 3 cạnh viền siêu mỏng của màn hình cho góc nhìn rộng và tôn lên sự chuyên nghiệp từ LG. Phần chân đế công thái học được làm hình vòng đảm bảo sự chắc chắn nhất định khi kết nối với phần màn hình của LG 34WR55QK-B.\n\n- Màn hình cong LG 34WR55QK-B 34\n\nNhắc đến công thái học, điều này được bao quanh LG 34WR55QK-B. Chính là đến từ chiếc chân đế với khớp nối nhanh cùng vô vàn khả năng tùy chỉnh lý tưởng. Nhờ vậy, bạn có thể dễ dàng tùy chỉnh góc nhìn màn hình cho phù hợp với ánh mắt và chiều cao người dùng. Từ đó, người dùng có thể thoải mái trang bị thêm các màn hình phụ cùng với chiếc arm màn hình để tạo nên không gian làm việc tối ưu và hiệu quả nhất có thể.\n\nKích thước rộng rãi, thoải mái cho đa tác vụ\nKích thước 34 inch mà LG 34WR55QK-B cho chúng ta không gian hiển thị đã vô cùng ấn tượng rồi, điều này còn được kết hợp theo độ cong 1800R và tỷ lệ màn hình 21:9 càng khiến cho người dùng chúng ta phải thốt lên wow vì góc nhìn trên chính chiếc màn hình LG của chúng ta. Qua đó, giúp bạn thoải mái sắp xếp cùng lúc nhiều công việc cùng lúc trên LG 34WR55QK-B mà không phải khó chịu vì kích thước quá nhỏ khi chia nhỏ.\n\n- Màn hình cong LG 34WR55QK-B 34\n\nĐa nhiệm đỉnh cao cùng ứng dụng LG Switch\nĐể đảm bảo hiệu quả làm việc lành mạch, LG 34WR55QK-B đem đến cho chúng ta khả năng hỗ trợ tuyệt đối từ ứng dụng LG Switch. Khi đó bạn có thể tối ưu hóa không giản hiển thị công việc với khả năng phân chia màn hình thành 6 phần rõ ràng hay dễ dàng thực hiện video call chỉ bằng những thao tác nhấn đơn giản. Chưa dừng lại đó, LG 34WR55QK-B hỗ trợ thêm cho chúng ta công nghệ PBP (ảnh kề ảnh) giúp cho hình ảnh đến 2 hay nhiều màn hình hoặc thiết bị khác nhu laptop không bị giảm thiểu về chất lượng.\n\n- Màn hình cong LG 34WR55QK-B 34\n\nNhững khung hình chất lượng\nVà không làm người dùng chúng ta thất vọng, LG tiếp tục mang đến một phiên bản LG 34WR55QK-B vô cùng cao cấp để cho ra những khung hình sắc nét và sống động. Đó là sự tuyệt vời của gam màu 99% sRGB tạo nên chiều sâu trong mọi chi tiết, hay đó còn là khả năng tương thích từ HDR10 phù hợp cho mọi nhu cầu sử dụng của chúng ta mà chẳng kém gì bất kì chiếc màn hình đồ họa nào trên thị trường.\n\n- Màn hình cong LG 34WR55QK-B 34\n\nDễ dàng kết nối với sự dễ dàng và thông dụng\nLG 34WR55QK-B đưa những cổng kết nối tốt nhất ra đằng sau với góc độ. Đặc biệt rằng khi ở đó chúng ta sẽ được trang bị cổng USB Type-C vô cùng tiện lợi mang đầy đủ tính năng như truyền dữ liệu, sạc pin cho các thiết bị khác như laptop hay thậm chí đó là truyền phát hình ảnh. Đương nhiên, chúng ta không thể quên các cổng kết nối đáp ứng nhu cầu truyền phát hình ảnh thông dụng như DisplayPort và HDMI 2.0 để phục vụ cho nhu cầu trình chiếu hay kết nối cùng phần cứng trong case máy tính như VGA.',
