INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình ASUS ProArt PA348CGV 34" IPS 2K 120Hz USBC chuyên đồ họa', 'man-hinh-asus-proart-pa348cgv-34-ips-2k-120hz-usbc-chuyen-do-hoa', 3,
        '{"Nhà sản xuất": "ASUS"}',
        'Màn hình ASUS ProArt PA348CGV là lựa chọn lý tưởng cho các chuyên gia sáng tạo, được thiết kế để mang đến trải nghiệm hình ảnh đỉnh cao và hiệu suất làm việc vượt trội. Với kích thước 34 inch và tấm nền IPS, màn hình này cung cấp góc nhìn rộng, màu sắc chân thực và độ tương phản ấn tượng, tái hiện mọi chi tiết với độ chính xác tuyệt đối. Độ phân giải 2K (3440 x 1440) Ultra-wide mang đến không gian làm việc rộng rãi, lý tưởng cho việc chỉnh sửa ảnh, dựng video, thiết kế đồ họa và đa nhiệm hiệu quả. Tần số quét 120Hz mượt mà, kết hợp với công nghệ Variable Refresh Rate (VRR), loại bỏ hiện tượng xé hình và giật lag, đảm bảo mọi chuyển động trên màn hình đều trơn tru, đặc biệt quan trọng khi làm việc với các ứng dụng đồ họa động hoặc chơi game. Khả năng kết nối đa dạng với cổng USB-C hỗ trợ DisplayPort Alternate Mode và Power Delivery lên đến 90W, cho phép truyền dữ liệu tốc độ cao, xuất hình ảnh và sạc thiết bị chỉ với một dây cáp duy nhất, giúp bàn làm việc gọn gàng và tiện lợi hơn. Chuẩn màu DCI-P3 98% và Delta E < 2 đảm bảo độ chính xác màu sắc cao, đáp ứng các tiêu chuẩn khắt khe nhất trong ngành công nghiệp sáng tạo. ASUS ProArt PA348CGV còn tích hợp công nghệ ASUS Splendid Video Intelligence và Calman Verified, mang đến những chế độ hiển thị tối ưu cho từng tác vụ, cùng với khả năng hiệu chuẩn chuyên nghiệp, sẵn sàng cho mọi dự án đòi hỏi sự tỉ mỉ và chính xác về màu sắc. Thiết kế công thái học với chân đế có thể điều chỉnh độ cao, nghiêng, xoay và trục xoay (pivot) mang lại sự thoải mái tối đa khi sử dụng trong thời gian dài.',
        1, 'https://product.hstatic.net/200000722513/product/asus_pa348cgv_gearvn_f21c5f0175e3442b9be34a21bf91c991.jpg'
    );
