INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình LG 45GS95QE-B UltraGear 45" OLED 2K 240Hz G-Sync', 'man-hinh-lg-45gs95qe-b-ultragear-45-oled-2k-240hz-g-sync', 12,
        '{"Kích thước màn hình": "45 inch", "Kiểu màn hình": "Màn hình cong", "Độ cong": "800R", "Tấm nền": "OLED", "Tỷ lệ khung hình": "21:9", "Độ phân giải": "3440 x 1440 pixels (UWQHD)", "Tần số quét": "240Hz", "Thời gian phản hồi": "0.03ms", "Độ phủ màu": "DCI-P3 98.5%", "Độ sáng": "Tiêu chuẩn 275 nit, tối đa 1300 nit", "Độ tương phản tĩnh": "1,500,000:1", "Công nghệ màn hình": "HDR10, PIP, PBP, VRR, Hiệu ứng HDR", "Cổng kết nối": "HDMI, DisplayPort, Đầu ra tai nghe", "Tính năng màn hình": "Hạn chế ánh sáng xanh, Chống chói, Chế độ chơi game, Chống nháy, Chế độ tiết kiệm điện, Tương thích NVIDIA G-Sync, AMD FreeSync Premium Pro, VESA DisplayHDR 400", "Tiện ích": "Tiết kiệm năng lượng thông minh, Hiệu chỉnh phần cứng, Chế độ đọc sách, Bộ đếm FPS"}',
        'LG UltraGear OLED 45GS95QE-B có tấm nền OLED cong 800R, mang đến màu sắc sống động. Ngoài ra còn có khả năng tương thích G-Sync và FreeSync, tối ưu cho trải nghiệm chơi game đỉnh cao. Điểm ngay những thế mạnh về siêu phẩm này ngay bên dưới nhé!\n\nĐộ phân giải cao cấp, hiệu suất tối ưu\nThiết bị sở hữu độ phân giải Ultra-WQHD, mang lại hình ảnh chi tiết và sống động cho mọi tựa game. Với tần số quét 240Hz, màn hình Gaming LG UltraGear OLED 45GS95QE-B 45 inch đảm bảo chuyển động êm ái, không bị giật trong các game hành động cần tốc độ cao. \n\nMàn hình Gaming LG UltraGear OLED 45GS95QE-B 45 inch - Chiến game siêu mượt 240Hz\n\nThời gian phản hồi 0.03ms kết hợp với công nghệ làm mới động giúp giảm hiện tượng xé hình, nâng cao trải nghiệm chơi game. Vì vậy, sản phẩm là dòng màn hình dành riêng cho gamer chuyên nghiệp cần hiệu suất hiển thị vượt trội.\n\nThiết kế tích hợp đèn Hexagon độc đáo\nUltraGear OLED 45 inch nổi bật với độ cong 800R và phần viền cực mỏng, tạo không gian chơi game lý tưởng. Màn hình Gaming LG UltraGear OLED 45GS95QE-B 45 inch có màu đen sang trọng, kết hợp đèn Hexagon phía sau, toát lên vẻ hiện đại. \n\nThiết kế tích hợp đèn Hexagon độc đáo\n\nPhần chân đế linh hoạt có thể điều chỉnh độ cao và nghiêng, xoay, mang lại sự thoải mái tối đa khi sử dụng. Tông màu đen sâu từ tấm nền OLED giúp nâng cao chất lượng hiển thị hình ảnh, làm nổi bật từng chi tiết.\n\nCông nghệ hình ảnh nâng cao\nNhờ tích hợp HDR10 và DisplayHDR True Black 400, thiết bị mang đến độ tương phản ấn tượng. Công nghệ hình ảnh này trên màn hình Gaming LG UltraGear OLED 45GS95QE-B 45 inch giúp tái hiện màu sắc rực rỡ và đen sâu tuyệt đối. \n\nCông nghệ hình ảnh nâng cao\n\nChế độ chống nháy cùng giảm ánh sáng xanh tích hợp có khả năng giữ mắt không mỏi khi sử dụng lâu. Những tính năng tiên tiến này của màn hình đảm bảo trải nghiệm gaming đẳng cấp và sự thoải mái cho game thủ.\n\nCổng kết nối rộng\nMàn hình LG này được trang bị 2 cổng HDMI 2.1 và 1 cổng DisplayPort 1.4, đáp ứng mọi nhu cầu kết nối gaming. Ngoài ra còn có khả năng tương thích với jack tai nghe 4 cực với âm thanh DTS Headphone:X, mang đến hiệu ứng 3D sống động. \n\nCổng kết nối rộng\n\nHầu hết các cổng kết nối đều hỗ trợ tần số quét cao, phù hợp cho PC và console hiện đại. Thiết kế cổng thông minh này góp phần giúp việc lắp đặt thiết bị trở nên tiện lợi và nhanh chóng.\n\nChế độ Crosshair hiển thị tâm độc đáo\nNgoài ra, màn hình còn tích hợp chế độ Crosshair, hiển thị tâm ngắm cố định giữa màn hình, hỗ trợ game thủ bắn súng chuẩn xác hơn. Chế độ còn cho phép tùy chỉnh kiểu dáng và màu sắc của tâm ngắm, phù hợp với từng tựa game FPS. \n\nChế độ Crosshair hiển thị tâm độc đáo\n\nNhờ đó, màn hình Gaming LG UltraGear OLED 45GS95QE-B 45 inch có thể cải thiện hiệu suất thi đấu, đặc biệt trong các trận chiến đòi hỏi phản xạ nhanh. Lợi thế cạnh tranh này giúp sản phẩm thêm nổi bật trong các dòng màn hình gaming cao cấp\n\nHỗ trợ đa nhiệm cho PBP và PIP\nUltraGear OLED 45 inch cung cấp chế độ Picture-by-Picture (PBP) và Picture-in-Picture (PIP), cho phép hiển thị đồng thời hai nguồn nội dung khác nhau. Với màn hình này, người dùng có thể vừa đánh game vừa xem hướng dẫn mà không cần thiết bị phụ. \n\nHỗ trợ đa nhiệm cho PBP và PIP\n\nTính năng này đặc biệt hữu ích cho streamer hoặc người dùng cần thực hiện đa nhiệm hiệu quả. Trải nghiệm này cần tận dụng tối đa màn hình rộng 21:9, nâng cao tối hiệu suất sử dụng.',
        1, 'https://product.hstatic.net/200000722513/product/lg_45gs95qe_oled_gearvn_03df3c02fdaa4615a1c9bf54a5e7579d.jpg'
    );
