INSERT INTO product (name, seo_name, brand_id, specifications, description, created_by, image_url) 
    VALUES (
        'Màn hình cong Samsung Odyssey NEO G9 LS57CG952 57" 4K 240Hz', 'man-hinh-cong-samsung-odyssey-neo-g9-ls57cg952-57-4k-240hz', 16,
        '{"Kiểu màn hình": "Màn hình cong", "Tấm nền": "VA", "Tỉ lệ màn hình": "32:9", "Độ phân giải": "7680 x 2160 pixels", "Tần số quét": "240Hz", "Thời gian phản hồi": "1ms", "Độ sáng": "420 nits", "Góc nhìn": "178°(H)/178°(V)", "Độ tương phản tĩnh": "2500:1 (Typ.)", "Công nghệ màn hình": "Quantum Matrix, VESA DisplayHDR 1000, AMD FreeSync Premium Pro, CoreSync, Core Lighting+", "Nghiêng": "-3° ~ 10° (±2°)", "Điều chỉnh độ cao": "HAS (chân đế có thể điều chỉnh độ cao)", "Treo tường": "100 x 100 mm", "Cổng kết nối": "1 x DisplayPort 2.1, 3 x HDMI 2.1, 1 x cổng tai nghe, 2 x USB Hub 3.0", "Hệ thống âm thanh": "Không", "Tính năng màn hình": "Chống chói, Chế độ chơi game, Chống nháy, AMD FreeSync Premium Pro", "Tiện ích": "Chế độ bảo vệ mắt, Chống nháy, Picture-by-Picture, Off Timer Plus, Screen Size Optimizer, Black Equalizer, Low Input Lag Mode, Refresh Rate Optimizer, Super Arena Gaming UX", "Công suất tiêu thụ": "300W", "Kích thước": "1327.5 x 601.0 x 499.6 mm (bao gồm chân đế)", "Trọng lượng": "Có chân đế: 19.0 kg, Không chân đế: 15.4 kg"}',
        'Màn hình cong Samsung Odyssey NEO G9 LS57CG952 57\" 4K 240Hz là đỉnh cao của công nghệ màn hình gaming, mang đến trải nghiệm nhập vai chưa từng có. Với kích thước siêu rộng 57 inch và độ cong 1000R, màn hình này bao trọn tầm nhìn của bạn, tạo cảm giác như đang thực sự bước vào thế giới game. Độ phân giải Dual UHD (7680x2160) mang lại hình ảnh sắc nét đến kinh ngạc, chi tiết đến từng điểm ảnh, cho phép bạn nhìn thấy mọi thứ rõ ràng hơn bao giờ hết. Tần số quét 240Hz siêu tốc cùng thời gian phản hồi 1ms (GtG) loại bỏ hoàn toàn hiện tượng nhòe hình, giật lag, đảm bảo mọi chuyển động trong game đều mượt mà và chính xác đến từng mili giây. Công nghệ Quantum Matrix Technology sử dụng đèn Mini-LED cho khả năng kiểm soát ánh sáng vượt trội, mang đến độ tương phản đỉnh cao, màu sắc sống động và độ sáng ấn tượng, tái hiện những cảnh game HDR chân thực và ngoạn mục. Hỗ trợ VESA DisplayHDR™ 2000, màn hình này có thể đạt độ sáng tối đa lên tới 2000 nits, làm nổi bật từng chi tiết trong cả vùng sáng nhất và tối nhất. Tính năng Quantum Dot và công nghệ chống lóa AR (Anti-Reflective) đảm bảo màu sắc rực rỡ, chính xác và giảm thiểu tối đa hiện tượng phản chiếu ánh sáng gây khó chịu. Multi-View cho phép bạn hiển thị đồng thời nhiều nguồn tín hiệu, biến màn hình khổng lồ này thành trung tâm giải trí đa nhiệm hiệu quả. Cổng kết nối đa dạng bao gồm HDMI 2.1 và DisplayPort 1.4, sẵn sàng cho các thiết bị gaming thế hệ mới nhất. Thiết kế Gaming Dome với hệ thống đèn LED RGB Aura Sync tùy chỉnh mang lại phong cách riêng biệt cho góc máy của bạn. Samsung Odyssey NEO G9 LS57CG952 không chỉ là một màn hình, mà là một tuyên bố về hiệu suất và công nghệ, dành cho những game thủ không thỏa hiệp.',
        1, 'https://product.hstatic.net/200000722513/product/vn-odyssey-neo-g9-g95nc-ls57cg952nexxv-537933911_3923e3e9cf374fafb9ead3a36151d0e4.png'
    );
